import React, { useEffect, useState, useMemo } from 'react';
import { Box, Typography, Paper, CircularProgress, Divider } from '@mui/material';
import {
    CheckCircle,
    ThumbUp,
    Cancel,
    Paid,
    Restore,
    PictureAsPdf,
    Email,
    Delete,
    History,
    Archive,
    Info,
    Edit,
    Add
} from '@mui/icons-material';
import { helpersWrapper } from '../utils/clientQueries';
import { collectionName as logsCollectionName } from './Management/manager_action_logs';

const ICON_MAP = {
    CheckCircle, ThumbUp, Cancel, Paid, Restore, PictureAsPdf, Email, Delete, History, Archive,
    'create': Add,
    'update': Edit,
    'default': Info
};

const ActionTimeline = ({ entityId, entityType }) => {
    const [logs, setLogs] = useState([]);
    const [loading, setLoading] = useState(true);

    const logsHelpers = useMemo(() => helpersWrapper(logsCollectionName), []);

    useEffect(() => {
        const fetchLogs = async () => {
            if (!entityId || !entityType) return;
            setLoading(true);
            try {
                // Fetch all logs and filter client-side (unless API supports filtering)
                // Assuming client-side filtering for now as per other components
                const allLogs = await logsHelpers.fetchItems();
                const relevantLogs = allLogs
                    .filter(log => log.entity_id === entityId && log.entity_type === entityType)
                    .sort((a, b) => new Date(b.created_at || b.timestamp) - new Date(a.created_at || a.timestamp)); // Newest first

                setLogs(relevantLogs);
            } catch (error) {
                console.error("Error fetching action logs:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchLogs();
    }, [entityId, entityType, logsHelpers]);

    if (loading) return <Box p={2} display="flex" justifyContent="center"><CircularProgress size={24} /></Box>;
    if (logs.length === 0) return null;

    return (
        <Box mt={4}>
            <Typography variant="h5" gutterBottom>Action History</Typography>
            <Paper sx={{ p: 2 }}>
                <Box sx={{ position: 'relative', pl: 2 }}>
                    {/* Vertical Line */}
                    <Box sx={{
                        position: 'absolute',
                        top: 16,
                        bottom: 16,
                        left: 28, // Center of the circle
                        width: 2,
                        bgcolor: 'grey.300',
                        zIndex: 0
                    }} />

                    {logs.map((log, index) => {
                        // Determine Icon
                        // Logic: verify action_type or status match specific icons
                        // fallback to 'default'
                        // If log.action_type is in dictionary?
                        // Assuming action_type might look like "Approve", "Send Email"
                        let IconComp = ICON_MAP.default;

                        // Heuristic for icons
                        const typeLower = (log.action_type || '').toLowerCase();
                        if (typeLower.includes('approv')) IconComp = ThumbUp;
                        else if (typeLower.includes('reject')) IconComp = Cancel;
                        else if (typeLower.includes('pay')) IconComp = Paid;
                        else if (typeLower.includes('email')) IconComp = Email;
                        else if (typeLower.includes('pdf')) IconComp = PictureAsPdf;
                        else if (typeLower.includes('delet')) IconComp = Delete;
                        else if (typeLower.includes('creat')) IconComp = Add;
                        else if (typeLower.includes('updat')) IconComp = Edit;
                        else if (typeLower.includes('archiv')) IconComp = Archive;
                        else if (typeLower.includes('restor')) IconComp = Restore;

                        return (
                            <Box key={log.id} sx={{ display: 'flex', mb: 3, position: 'relative', zIndex: 1 }}>
                                {/* Circle Node */}
                                <Box sx={{
                                    width: 32,
                                    height: 32,
                                    borderRadius: '50%',
                                    bgcolor: 'white',
                                    border: '2px solid',
                                    borderColor: 'primary.main',
                                    color: 'primary.main',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    mr: 2,
                                    flexShrink: 0
                                }}>
                                    <IconComp fontSize="small" />
                                </Box>

                                {/* Content */}
                                <Box sx={{ flexGrow: 1, pt: 0.5 }}>
                                    <Box display="flex" justifyContent="space-between" alignItems="flex-start">
                                        <Typography variant="subtitle1" fontWeight="bold">
                                            {log.action_type || 'Unknown Action'}
                                        </Typography>
                                        <Typography variant="caption" color="textSecondary">
                                            {log.timestamp ? new Date(log.timestamp).toLocaleString() : (log.created_at ? new Date(log.created_at).toLocaleString() : '')}
                                        </Typography>
                                    </Box>

                                    <Typography variant="body2" color="textSecondary">
                                        Performed by: {log.accountable_id || 'System'}
                                    </Typography>

                                    {log.details && (
                                        <Box mt={0.5} p={1} bgcolor="grey.100" borderRadius={1}>
                                            <Typography variant="caption" sx={{ display: 'block', whiteSpace: 'pre-wrap' }}>
                                                {log.details}
                                            </Typography>
                                        </Box>
                                    )}

                                    {log.status === 'failed' && (
                                        <Typography variant="caption" color="error" sx={{ mt: 0.5, display: 'block' }}>
                                            Status: Failed
                                        </Typography>
                                    )}
                                </Box>
                            </Box>
                        );
                    })}
                </Box>
            </Paper>
        </Box>
    );
};

export default ActionTimeline;

import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, TextField, DialogActions, Button, Box, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const neonBlue = '#00f3ff';
const neonRed = '#ff0055';
const darkBg = '#050505';

const LoginDialog = ({ open, onClose }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleLogin = async () => {
    setError('');
    try {
      const isSuccess = await login(email, password);
      if (isSuccess) {
        onClose();
        navigate('/summary'); // Or dashboard
      } else {
        setError('Invalid credentials');
      }
    } catch (error) {
      console.error('Login failed:', error);
      setError('Login failed. Please try again.');
    }
  };

  const textFieldSx = {
    my: 2,
    '& .MuiOutlinedInput-root': {
      '& fieldset': { borderColor: '#333' },
      '&:hover fieldset': { borderColor: neonBlue },
      '&.Mui-focused fieldset': { borderColor: neonBlue, boxShadow: `0 0 10px ${neonBlue}` },
    },
    '& .MuiInputLabel-root': { color: '#aaa' },
    '& .MuiInputLabel-root.Mui-focused': { color: neonBlue },
    '& .MuiInputBase-input': { color: '#fff' },
  };

  return (
    <Dialog
      open={open}
      onClose={onClose}
      PaperProps={{
        sx: {
          backgroundColor: darkBg,
          border: `1px solid ${neonBlue}`,
          boxShadow: `0 0 20px rgba(0, 243, 255, 0.2)`,
          color: '#fff',
          minWidth: '350px'
        }
      }}
    >
      <DialogTitle sx={{ color: '#fff', textAlign: 'center', fontWeight: 'bold' }}>
        Login
      </DialogTitle>
      <DialogContent>
        {error && (
          <Typography variant="body2" color={neonRed} sx={{ textAlign: 'center', mb: 1 }}>
            {error}
          </Typography>
        )}
        <TextField
          label="Email"
          type="email"
          fullWidth
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          sx={textFieldSx}
        />
        <TextField
          label="Password"
          type="password"
          fullWidth
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          sx={textFieldSx}
        />
      </DialogContent>
      <DialogActions sx={{ justifyContent: 'center', pb: 3 }}>
        <Button onClick={onClose} sx={{ color: '#aaa', '&:hover': { color: '#fff' } }}>Cancel</Button>
        <Button
          onClick={handleLogin}
          variant="contained"
          sx={{
            backgroundColor: neonBlue,
            color: '#000',
            fontWeight: 'bold',
            '&:hover': { backgroundColor: '#fff', boxShadow: `0 0 15px ${neonBlue}` }
          }}
        >
          Login
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default LoginDialog;

import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Grid,
  Alert,
  Paper,
  AppBar,
  Toolbar,
} from "@mui/material";
import { useNavigate, Link } from "react-router-dom";
import ConfirmationModal from "./ConfirmationModal";
import { useAuth } from "../../contexts/AuthContext"; // Use AuthContext

const neonBlue = '#00f3ff';
const neonRed = '#ff0055';
const darkBg = '#050505';

const OrganizationRegistration = () => {
  const [orgName, setOrgName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [domain, setDomain] = useState("");
  const [numUsers, setNumUsers] = useState(1);
  const [numStores, setNumStores] = useState(1);
  const [error, setError] = useState(null);
  const [confirmationOpen, setConfirmationOpen] = useState(false);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const { registerOrganization } = useAuth(); // Use context method

  const userPrice = 50;
  const storePrice = 100;
  const basePrice = 500;
  const totalPrice = basePrice + numUsers * userPrice + numStores * storePrice;

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      await registerOrganization({
        orgName,
        firstName,
        lastName,
        email,
        password,
        domain,
        numUsers,
        numStores,
        totalPrice,
      });

      setConfirmationOpen(true);
    } catch (err) {
      console.error("Error registering organization:", err);
      setError(
        err.response?.data?.message ||
        "An error occurred during registration. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  const handleConfirm = () => {
    setConfirmationOpen(false);
    navigate("/summary"); // Go to dashboard after confirm
  };

  const textFieldSx = {
    '& .MuiOutlinedInput-root': {
      '& fieldset': { borderColor: '#333' },
      '&:hover fieldset': { borderColor: neonBlue },
      '&.Mui-focused fieldset': { borderColor: neonBlue, boxShadow: `0 0 10px ${neonBlue}` },
    },
    '& .MuiInputLabel-root': { color: '#aaa' },
    '& .MuiInputLabel-root.Mui-focused': { color: neonBlue },
    '& .MuiInputBase-input': { color: '#fff' },
  };

  return (
    <Box sx={{ backgroundColor: darkBg, minHeight: '100vh', color: '#fff', pb: 10 }}>
      <Container maxWidth="sm">
        <AppBar position="fixed" elevation={0} sx={{ backgroundColor: 'rgba(5,5,5,0.9)', borderBottom: '1px solid #333' }}>
          <Toolbar>
            <Button component={Link} to="/" sx={{ color: '#fff', textTransform: 'none', fontSize: '1.2rem', fontWeight: 'bold' }}>
              Vault Insight
            </Button>
          </Toolbar>
        </AppBar>
        <Toolbar />
        <Paper
          elevation={0}
          sx={{
            p: 4,
            mt: 5,
            backgroundColor: '#0a0a0a',
            border: `1px solid ${neonBlue}`,
            boxShadow: `0 0 30px rgba(0, 243, 255, 0.1)`,
            color: '#fff'
          }}
        >
          <Typography variant="h4" gutterBottom sx={{ textAlign: 'center', mb: 4, fontWeight: 'bold' }}>
            Register Organization
          </Typography>
          <form onSubmit={handleRegister}>
            <Box sx={{ mb: 3 }}>
              <TextField
                fullWidth
                label="Organization Name"
                variant="outlined"
                value={orgName}
                onChange={(e) => setOrgName(e.target.value)}
                required
                sx={textFieldSx}
              />
            </Box>
            <Grid container spacing={2} sx={{ mb: 1 }}>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="First Name"
                  variant="outlined"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  required
                  sx={textFieldSx}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="Last Name"
                  variant="outlined"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                  required
                  sx={textFieldSx}
                />
              </Grid>
            </Grid>

            <Box sx={{ mb: 3, mt: 3 }}>
              <TextField
                fullWidth
                label="Organization Email"
                type="email"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                sx={textFieldSx}
              />
            </Box>
            <Box sx={{ mb: 3 }}>
              <TextField
                fullWidth
                label="Password"
                type="password"
                variant="outlined"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                sx={textFieldSx}
              />
            </Box>
            <Box sx={{ mb: 3 }}>
              <TextField
                fullWidth
                label="Custom Domain"
                variant="outlined"
                value={domain}
                onChange={(e) => setDomain(e.target.value)}
                required
                sx={textFieldSx}
                helperText='e.g. "mycompany" (creates mycompany.vaultinsight.com)'
                FormHelperTextProps={{ sx: { color: '#666' } }}
              />
            </Box>
            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="Number of Users"
                  type="number"
                  variant="outlined"
                  value={numUsers}
                  onChange={(e) =>
                    setNumUsers(Math.max(1, parseInt(e.target.value, 10) || 1))
                  }
                  inputProps={{ min: 1 }}
                  required
                  sx={textFieldSx}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="Number of Stores"
                  type="number"
                  variant="outlined"
                  value={numStores}
                  onChange={(e) =>
                    setNumStores(Math.max(1, parseInt(e.target.value, 10) || 1))
                  }
                  inputProps={{ min: 1 }}
                  required
                  sx={textFieldSx}
                />
              </Grid>
            </Grid>
            <Box sx={{ mb: 3, p: 2, border: '1px solid #333', borderRadius: 2 }}>
              <Typography variant="h6" sx={{ color: neonBlue }}>Pricing</Typography>
              <Typography sx={{ color: '#aaa' }}>Base Price: €{basePrice}</Typography>
              <Typography sx={{ color: '#aaa' }}>Price per User: €{userPrice}</Typography>
              <Typography sx={{ color: '#aaa' }}>Price per Store: €{storePrice}</Typography>
              <Typography variant="h6" sx={{ mt: 1, color: '#fff' }}>
                Total Price: <strong>€{totalPrice}</strong>
              </Typography>
            </Box>

            {error && (
              <Alert severity="error" sx={{ mb: 3 }}>
                {error}
              </Alert>
            )}

            <Button
              type="submit"
              variant="contained"
              fullWidth
              disabled={loading}
              sx={{
                backgroundColor: neonBlue,
                color: '#000',
                fontWeight: 'bold',
                padding: '12px',
                '&:hover': { backgroundColor: '#fff', boxShadow: `0 0 20px ${neonBlue}` }
              }}
            >
              {loading ? "Processing..." : "Register & Pay"}
            </Button>
          </form>
        </Paper>

        <ConfirmationModal
          open={confirmationOpen}
          onClose={handleConfirm}
          onConfirm={handleConfirm}
        />
      </Container>
    </Box>
  );
};

export default OrganizationRegistration;

import React from 'react';
import { Box, Table, TableBody, Checkbox, TableCell, TableContainer, TablePagination, TableRow, Tooltip } from '@mui/material';
import WarningIcon from '@mui/icons-material/Warning';
import EditIcon from '@mui/icons-material/Edit';
import VisibilityIcon from '@mui/icons-material/Visibility';
import { IconButton } from '@mui/material';

import BaseTableHead from './BaseTableHead';

function descendingComparator(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

function getComparator(order, orderBy) {
  return order === 'desc'
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

function stableSort(array, comparator) {
  const stabilizedThis = array.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) return order;
    return a[1] - b[1];
  });
  return stabilizedThis.map((el) => el[0]);
}

function BaseTable({
  items,
  order,
  setOrder,         // Add this to receive the prop
  orderBy,
  setOrderBy,       // Add this to receive the prop
  selected,
  setSelected,
  page,
  setPage,
  rowsPerPage,
  setRowsPerPage,
  dense,
  setDense,
  fieldConfig,
  validationRules = [], // Receive rules
  onViewItem, // New Prop
  onEditItem, // New Prop
  hasMore = false, // Added prop for pagination
}) {

  const validateRow = (row) => {
    // ... (logic remains same, validationRules uses it)
    // To save context, we just return empty array if no rules
    if (!validationRules || validationRules.length === 0) return [];

    // ... actually validateRow implementation is lengthy. 
    // I should only replace the START of the function to add props.
    // But since I am replacing the header logic too, I can do it in chunks.


    // ... (Assuming validateRow is unchanged, I will skip replacing it if possible)
    // But I need to add props to the destructuring at the TOP. 
    // And fix the BaseTableHead usage at the BOTTOM.

    // Let's do 2 chunks.

    // CHUNK 1: Props
    // CHUNK 2: Header Logic

    const issues = [];
    const relevantRules = validationRules.filter(r =>
      r.current_step === row.processing_step || r.current_step === '*'
    );

    relevantRules.forEach(rule => {
      let isValid = true;
      const val = row[rule.rule_value] ?? row[rule.action_name]; // Try rule_value as field name, fallback to action? 
      // Wait, rule_value is likely the field Name (e.g. 'total_price'). 
      // But for 'value_greater_than', we need a Value to compare against.
      // The current schema has `rule_value` as ONE column. 
      // For comparisons we need Target Field AND Target Value.
      // Current schema: rule_value "e.g. invoice_number (for required field) or total_amount < 5000"
      // If the user inputs "total_amount 5000" in rule_value, we need to parse it?
      // The User Request said "logical rule to a column... predefined in fieldsConfig... "

      // Let's assume standard parsing or just check 'required_field' for now to be safe, 
      // AND implement basic 'value comparisons' assuming rule_value holds the Reference Value 
      // and we need another field for "Target Column"? 
      // The schema in entity_workflow_rules doesn't have "Target Column". 
      // It has "rule_value". 
      // Maybe `rule_value` holds "ColumnName,TargetValue"? 
      // Or maybe I should interpret `action_name` as "Column Name" for these rules?
      // Re-reading config: `action_name` label "Action Name". `rule_value` label "Rule Value / Condition".

      // Heuristic:
      // Rule Type: "value_greater_than"
      // Action Name: "total_price" (The column to check)
      // Rule Value: "1000" (The threshold)

      const columnToCheck = rule.action_name;
      const threshold = rule.rule_value;
      const rowValue = row[columnToCheck];

      switch (rule.rule_type) {
        case 'required_field':
          // here columnToCheck is the field
          if (!rowValue && rowValue !== 0) isValid = false;
          break;
        case 'value_equals':
          if (String(rowValue) !== String(threshold)) isValid = false;
          break;
        case 'value_less_than':
          if (Number(rowValue) >= Number(threshold)) isValid = false;
          break;
        case 'value_greater_than':
          if (Number(rowValue) <= Number(threshold)) isValid = false;
          break;
        case 'value_between':
          const [min, max] = threshold.split(',').map(Number);
          if (Number(rowValue) < min || Number(rowValue) > max) isValid = false;
          break;
        // Approvals - mock check for field existence or specific value
        case 'approval_email':
        case 'approval_manager':
          // Assume we check if 'approved_by' is set?
          if (!row['accountable_id']) isValid = false; // logic placeholder
          break;
        default:
          break;
      }

      if (!isValid) {
        issues.push(rule.error_message || `Validation failed: ${rule.rule_type} on ${columnToCheck}`);
      }
    });
    return issues;
  };

  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelected = items.map((n) => n.id);
      setSelected(newSelected);
      return;
    }
    setSelected([]);
  };

  const handleClick = (event, id) => {
    const selectedIndex = selected.indexOf(id);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(selected.slice(0, selectedIndex), selected.slice(selectedIndex + 1));
    }
    setSelected(newSelected);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const isSelected = (id) => selected.indexOf(id) !== -1;

  const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - items.length) : 0;

  const visibleRows = React.useMemo(() => stableSort(items, getComparator(order, orderBy)).slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage), [items, order, orderBy, page, rowsPerPage]);

  return (
    <Box>
      <TableContainer>
        <Table sx={{ minWidth: 750 }} size={dense ? 'small' : 'medium'}>
          <BaseTableHead
            headCells={[
              ...Object.keys(fieldConfig).map((key) => ({
                id: key,
                label: fieldConfig[key].label,
              }))
            ]}
            order={order}
            orderBy={orderBy}
            onRequestSort={handleRequestSort}
            onSelectAllClick={handleSelectAllClick}
            numSelected={selected.length}
            rowCount={items.length}
          />
          <TableBody>
            {visibleRows.map((row, index) => {
              const isItemSelected = isSelected(row.id);
              const labelId = `enhanced-table-checkbox-${index}`;

              return (
                <TableRow
                  hover
                  onClick={(event) => handleClick(event, row.id)}
                  role="checkbox"
                  aria-checked={isItemSelected}
                  tabIndex={-1}
                  key={row.id}
                  selected={isItemSelected}
                >
                  <TableCell padding="checkbox">
                    <Box display="flex" alignItems="center">
                      <Checkbox color="primary" checked={isItemSelected} inputProps={{ 'aria-labelledby': labelId }} />
                      {validateRow(row).length > 0 && (
                        <Tooltip title={validateRow(row).join('\n')}>
                          <WarningIcon color="error" fontSize="small" />
                        </Tooltip>
                      )}
                    </Box>
                  </TableCell>

                  {Object.keys(fieldConfig).map((field) => (
                    <TableCell
                      size="medium"
                      key={field}
                      align={fieldConfig[field].numeric ? 'right' : 'left'}
                      sx={{ maxWidth: 300, whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }} // Set maxWidth and text overflow styles
                    >
                      {row[field]}
                    </TableCell>
                  ))}

                </TableRow>
              );
            })}
            {emptyRows > 0 && (
              <TableRow style={{ height: (dense ? 33 : 53) * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[15, 25, 50]}
        component="div"
        // If we have more data, pretend we have at least one more page
        count={hasMore ? items.length + 1 : items.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </Box>
  );
}
