                            ))}
                            {forecastData.length === 0 && (
                                <ListItem>
                                    <ListItemText
                                        primary="No payments scheduled"
                                        secondary="No items due in the selected period"
                                    />
                                </ListItem>
                            )}
                        </List>
                    </Paper>
                </Grid>

                {/* Detailed Table */}
                <Grid item xs={12} md={6}>
                    <Paper sx={{ maxHeight: 600, overflow: 'auto' }}>
                        <TableContainer>
                            <Table stickyHeader>
                                <TableHead>
