                                        </TableRow>
                                    ))}
                                    {forecastData.length === 0 && (
                                        <TableRow>
                                            <TableCell colSpan={4} align="center">
                                                No payments due in the selected period
                                            </TableCell>
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    );
};

