import { addDays, endOfMonth, isMatch, format } from 'date-fns';

export const TermCalculator = {
    /**
     * Calculates the due date based on the term configuration and a base date.
     * @param {Object} termConfig - The payment term configuration object (from PaymentTerms.js schema).
     * @param {Date|string} baseDateInput - The starting date (Invoice Date, GRN Date, etc.).
     * @returns {Date} The calculated due date.
     */
    calculateDueDate: (termConfig, baseDateInput) => {
        if (!termConfig || !baseDateInput) return null;

        const baseDate = new Date(baseDateInput);
        let dueDate = new Date(baseDate);

        // 1. Apply Logic based on due_date_method
        switch (termConfig.due_date_method) {
            case 'fixed_days':
                // Standard "Net X Days"
                dueDate = addDays(baseDate, termConfig.days_due || 0);
                break;

            case 'end_of_month':
                // "EOM" or "Net X EOM"
                // Logic: "End of the month after X days offset".
                const intermediateDate = addDays(baseDate, termConfig.days_due || 0);

                // Check Cutoff if defined
                if (termConfig.cutoff_day && baseDate.getDate() > termConfig.cutoff_day) {
                    // If past cutoff, add an extra month logic?
                    // Standard EOM often implies: If before 25th, end of this month. If after, end of next.
                    // Here we treat cutoff generally as "move to next cycle"
                    // Ideally we'd use 'addMonths' but simple setMonth works for now.
                    intermediateDate.setMonth(intermediateDate.getMonth() + 1);
                }

                dueDate = endOfMonth(intermediateDate);
                break;

            case 'day_of_month':
                // "15th of next month"
                // Logic: Go to next month (or offset months), set date to X.
                dueDate.setMonth(dueDate.getMonth() + 1);
                dueDate.setDate(termConfig.days_due || 15);
                break;

            case 'fixed_date':
                // Specific date
                // Ideally this would come from a specific 'fixed_date' field in the invoice or term, 
                // but for generic terms it's rare.
                break;

            default:
                break;
        }

        return dueDate;
    },

    // Helper to format consistent dates
    formatDate: (date) => {
        return date ? format(date, 'yyyy-MM-dd') : '';
    }
};

import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, IconButton, Table, TableBody, TableCell, TableHead, TableRow, TextField, Paper } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

/**
 * A generic internal editor for JSON array rules (Discounts, Installments).
 * @param {Array} value - Current value (array of objects)
 * @param {Function} onChange - Callback (newValue)
 * @param {string} type - 'discount' | 'installment'
 */
const TermRuleEditor = ({ value, onChange, type }) => {
    const [rows, setRows] = useState([]);

    useEffect(() => {
        if (Array.isArray(value)) {
            setRows(value);
        } else {
            setRows([]);
        }
    }, [value]);

    const handleAdd = () => {
        const newRow = type === 'discount'
            ? { days: 10, percent: 2 }
            : { percent: 50, days_offset: 0 };
        const newRows = [...rows, newRow];
        setRows(newRows);
        onChange(newRows);
    };

    const handleDelete = (index) => {
        const newRows = rows.filter((_, i) => i !== index);
        setRows(newRows);
        onChange(newRows);
    };

    const handleChange = (index, field, val) => {
        const newRows = [...rows];
        newRows[index] = { ...newRows[index], [field]: parseFloat(val) || 0 };
        setRows(newRows);
        onChange(newRows);
    };

    return (
        <Paper variant="outlined" sx={{ p: 2, mb: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
                {type === 'discount' ? 'Early Payment Discounts' : 'Installment Schedule'}
            </Typography>

            <Table size="small">
                <TableHead>
                    <TableRow>
                        {type === 'discount' ? (
                            <>
                                <TableCell>Within Days</TableCell>
                                <TableCell>Discount %</TableCell>
                            </>
                        ) : (
                            <>
                                <TableCell>% of Total</TableCell>
                                <TableCell>Days Offset</TableCell>
                            </>
                        )}
                        <TableCell width={50}></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row, index) => (
                        <TableRow key={index}>
                            {type === 'discount' ? (
                                <>
                                    <TableCell>
                                        <TextField
                                            size="small"
                                            type="number"
                                            value={row.days}
                                            onChange={(e) => handleChange(index, 'days', e.target.value)}
                                        />
                                    </TableCell>
                                    <TableCell>
                                        <TextField
                                            size="small"
                                            type="number"
                                            value={row.percent}
                                            onChange={(e) => handleChange(index, 'percent', e.target.value)}
                                        />
                                    </TableCell>
                                </>
                            ) : (
                                <>
                                    <TableCell>
                                        <TextField
                                            size="small"
                                            type="number"
                                            value={row.percent}
                                            onChange={(e) => handleChange(index, 'percent', e.target.value)}
                                        />
                                    </TableCell>
                                    <TableCell>
                                        <TextField
                                            size="small"
                                            type="number"
                                            value={row.days_offset}
                                            onChange={(e) => handleChange(index, 'days_offset', e.target.value)}
                                        />
                                    </TableCell>
                                </>
                            )}
                            <TableCell>
                                <IconButton size="small" onClick={() => handleDelete(index)}>
                                    <DeleteIcon fontSize="small" />
                                </IconButton>
                            </TableCell>
                        </TableRow>
                    ))}
                    {rows.length === 0 && (
                        <TableRow>
                            <TableCell colSpan={3} align="center">
                                <Typography variant="caption" color="textSecondary">
                                    No rules defined.
                                </Typography>
                            </TableCell>
                        </TableRow>
                    )}
                </TableBody>
            </Table>
            <Button startIcon={<AddIcon />} size="small" onClick={handleAdd} sx={{ mt: 1 }}>
                Add Rule
            </Button>
        </Paper>
    );
};

export default TermRuleEditor;

import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper, Grid, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, IconButton, Chip, TextField, Alert } from '@mui/material';
import { LinkOff, Link as LinkIcon, CompareArrows, CheckCircle } from '@mui/icons-material';

const MatchingEngine = ({ invoiceAmount, invoiceId }) => {
    // Mock Data for POs
    const [mockPOs, setMockPOs] = useState([
        { id: 'PO-10234', vendor: 'Acme Corp Supply', date: '2023-10-15', total: 4500.00, status: 'Open' },
        { id: 'PO-10255', vendor: 'Acme Corp Supply', date: '2023-10-20', total: 1250.00, status: 'Received' },
    ]);

    const [selectedPO, setSelectedPO] = useState(null);
    const [variance, setVariance] = useState(0);

    const handleSelectPO = (po) => {
        if (selectedPO?.id === po.id) {
            setSelectedPO(null); // Deselect
        } else {
            setSelectedPO(po);
        }
    };

    useEffect(() => {
        if (selectedPO && invoiceAmount) {
            const diff = parseFloat(invoiceAmount) - selectedPO.total;
            setVariance(diff.toFixed(2));
        } else {
            setVariance(0);
        }
    }, [selectedPO, invoiceAmount]);

    return (
        <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
            <Box display="flex" alignItems="center" mb={2}>
                <CompareArrows color="primary" sx={{ mr: 1 }} />
                <Typography variant="h6">PO Matching Engine</Typography>
            </Box>

            {!selectedPO ? (
                <Box>
                    <Typography variant="subtitle2" gutterBottom>Suggested Purchase Orders</Typography>
                    <TableContainer component={Paper} variant="outlined">
                        <Table size="small">
                            <TableHead>
                                <TableRow>
                                    <TableCell>PO Number</TableCell>
                                    <TableCell>Vendor</TableCell>
                                    <TableCell>Date</TableCell>
                                    <TableCell align="right">Total</TableCell>
                                    <TableCell>Status</TableCell>
                                    <TableCell align="center">Action</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {mockPOs.map((po) => (
                                    <TableRow key={po.id} hover>
                                        <TableCell component="th" scope="row">{po.id}</TableCell>
                                        <TableCell>{po.vendor}</TableCell>
                                        <TableCell>{po.date}</TableCell>
                                        <TableCell align="right">${po.total.toFixed(2)}</TableCell>
                                        <TableCell><Chip size="small" label={po.status} color={po.status === 'Received' ? 'success' : 'default'} /></TableCell>
                                        <TableCell align="center">
                                            <Button
                                                size="small"
                                                variant="outlined"
                                                startIcon={<LinkIcon />}
                                                onClick={() => handleSelectPO(po)}
                                            >
                                                Match
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Box>
            ) : (
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <Alert severity="info" action={
                            <Button color="inherit" size="small" onClick={() => setSelectedPO(null)}>Unlink</Button>
                        }>
                            Linked to <strong>{selectedPO.id}</strong>
                        </Alert>
                    </Grid>

                    {/* Comparison View */}
                    <Grid item xs={12} md={6}>
                        <Paper variant="outlined" sx={{ p: 2 }}>
                            <Typography variant="subtitle2" color="textSecondary">INVOICE</Typography>
                            <Typography variant="h4">${parseFloat(invoiceAmount || 0).toFixed(2)}</Typography>
                            <Typography variant="caption">Total Amount</Typography>
                        </Paper>
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <Paper variant="outlined" sx={{ p: 2 }}>
                            <Typography variant="subtitle2" color="textSecondary">PURCHASE ORDER ({selectedPO.id})</Typography>
                            <Typography variant="h4">${selectedPO.total.toFixed(2)}</Typography>
                            <Typography variant="caption">Total Value</Typography>
                        </Paper>
                    </Grid>

                    {/* Variance Analysis */}
                    <Grid item xs={12}>
                        <Box p={2} sx={{ bgcolor: Math.abs(variance) > 0 ? '#fff3e0' : '#e8f5e9', borderRadius: 1, border: '1px solid', borderColor: Math.abs(variance) > 0 ? 'warning.main' : 'success.main' }}>
                            <Box display="flex" justifyContent="space-between" alignItems="center">
                                <Box>
                                    <Typography variant="subtitle1" fontWeight="bold">
                                        Variance: {variance > 0 ? '+' : ''}{variance}
                                    </Typography>
                                    <Typography variant="body2">
                                        {Math.abs(variance) === 0 ? "Perfect Match (3-Way)" : "Price/Quantity Discrepancy Detected"}
                                    </Typography>
                                </Box>
                                <Button
                                    variant="contained"
                                    color={Math.abs(variance) > 0 ? "warning" : "success"}
                                    startIcon={Math.abs(variance) === 0 ? <CheckCircle /> : null}
                                >
                                    {Math.abs(variance) === 0 ? "Confirm Match" : "Request Approval"}
                                </Button>
                            </Box>
                        </Box>
                    </Grid>
                </Grid>
            )}
        </Paper>
    );
};

