import React, { useState, useEffect, useMemo } from 'react';
import {
    Box,
    Paper,
    Typography,
    Grid,
    TextField,
    Button,
    IconButton,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    MenuItem,
    Divider,
    Tabs,
    Tab,
    Alert,
    Tooltip
} from '@mui/material';
import {
    Add as AddIcon,
    Delete as DeleteIcon,
    Save as SaveIcon,
    PostAdd as PostIcon,
    CheckCircle as CheckCircleIcon,
    ArrowBack as ArrowBackIcon,
    ContentCopy as CopyIcon
} from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';

import { JournalEntryService } from './JournalEntryService';
import { helpersWrapper } from '../../../../../../utils/clientQueries';

// Mock Data for Selectors (Ideally fetched from backend/config)
const ACCOUNTS = [
    { id: '1001', code: '1001', name: 'Cash on Hand' },
    { id: '1002', code: '1002', name: 'Bank Account - Checking' },
    { id: '1100', code: '1100', name: 'Accounts Receivable' },
    { id: '2000', code: '2000', name: 'Accounts Payable' },
    { id: '4000', code: '4000', name: 'Sales Revenue' },
    { id: '5000', code: '5000', name: 'Office Supplies Expense' },
    { id: '5010', code: '5010', name: 'Rent Expense' },
    { id: '5020', code: '5020', name: 'Utilities Expense' },
];

const CURRENCIES = ['USD', 'EUR', 'GBP', 'CAD'];

const JournalEntryManager = () => {
    const navigate = useNavigate();
    const { id } = useParams(); // If editing existing
    const location = window.location.href; // Quick check for route context
    const isAllocation = location.includes('allocations');
    const isIntercompany = location.includes('intercompany');

    const [journal, setJournal] = useState(JournalEntryService.createEmptyJournal());

    // Set default category if creating new allocation
    useEffect(() => {
        if (!id || id === 'new') {
            if (isAllocation) {
                setJournal(prev => ({ ...prev, journal_category: 'allocation', journal_source: 'allocation' }));
            } else if (isIntercompany) {
                setJournal(prev => ({ ...prev, journal_category: 'intercompany', is_intercompany: true }));
            }
        }
    }, [id, isAllocation, isIntercompany]);

    const [loading, setLoading] = useState(false);
    const [statusMessage, setStatusMessage] = useState(null);
    const [tabValue, setTabValue] = useState(0);

    // --- Initialization ---
    useEffect(() => {
        if (id && id !== 'new') {
            loadJournal(id);
        }
    }, [id]);

    const loadJournal = async (journalId) => {
        setLoading(true);
        try {
            const api = helpersWrapper('journal_entries');
            const data = await api.fetchItemById(journalId);
            if (data) {
                // Parse lines if they are stored as JSON stirng or ensure structure
                // Assuming standard structure for now
                setJournal(data);
            }
        } catch (error) {
            console.error('Failed to load journal', error);
            setStatusMessage({ type: 'error', text: 'Failed to load journal entry.' });
        } finally {
            setLoading(false);
        }
    };

    // --- Computations ---
    const totals = useMemo(() => {
        let debit = 0;
        let credit = 0;
        (journal.lines || []).forEach(line => {
            debit += parseFloat(line.debit) || 0;
            credit += parseFloat(line.credit) || 0;
        });
        return {
            debit: Math.round(debit * 100) / 100,
            credit: Math.round(credit * 100) / 100,
            difference: Math.round((debit - credit) * 100) / 100
        };
    }, [journal.lines]);

    const isBalanced = totals.difference === 0 && (totals.debit > 0 || totals.credit > 0);

    // --- Handlers ---

    const handleHeaderChange = async (field, value) => {
        // Subledger Warning
        if (field === 'journal_source' && (value === 'ar' || value === 'ap' || value === 'payroll' || value === 'inventory')) {
            setStatusMessage({ type: 'warning', text: `Warning: Journals from ${value.toUpperCase()} should typically be managed in their respective subledgers.` });
        }

        setJournal(prev => ({ ...prev, [field]: value }));

        // If currency changes, fetch new exchange rate
        if (field === 'currency_code') {
            const rate = await JournalEntryService.getExchangeRate(value);
            setJournal(prev => ({ ...prev, exchange_rate: rate }));
        }
    };

    const handleLineChange = (index, field, value) => {
        const newLines = [...journal.lines];

        // Special handling: if debit is set, clear credit and vice versa
        if (field === 'debit' && value > 0) newLines[index].credit = 0;
        if (field === 'credit' && value > 0) newLines[index].debit = 0;

        newLines[index] = { ...newLines[index], [field]: value };
        setJournal(prev => ({ ...prev, lines: newLines }));
    };

    const addLine = () => {
        const newLine = {
            id: Date.now(), // temp id
            account_id: '',
            description: journal.description || '', // Default to header desc
            debit: 0,
            credit: 0,
            cost_center: '',
            project_id: ''
        };
        setJournal(prev => ({ ...prev, lines: [...prev.lines, newLine] }));
    };

    const removeLine = (index) => {
        if (journal.lines.length <= 2) return; // Maintain min 2 lines
        const newLines = journal.lines.filter((_, i) => i !== index);
        setJournal(prev => ({ ...prev, lines: newLines }));
    };

    const handleValidate = () => {
        const validation = JournalEntryService.validateJournal(journal);
        if (validation.isValid) {
            setStatusMessage({ type: 'success', text: 'Validation Successful! Journal is ready.' });
        } else {
            setStatusMessage({ type: 'error', text: 'Validation Failed: ' + validation.errors.join(' | ') });
        }
    };

    const handleSave = async (asPost = false) => {
        const validation = JournalEntryService.validateJournal(journal);
        if (!validation.isValid) {
            setStatusMessage({ type: 'error', text: validation.errors.join(' ') });
            return;
        }

        if (asPost) {
            // Simulate backend check
            const simulation = await JournalEntryService.simulatePosting(journal);
            if (!simulation.success) {
                setStatusMessage({ type: 'error', text: simulation.errors.join(' ') });
                return;
            }
        }

        setLoading(true);
        try {
            const api = helpersWrapper('journal_entries');

            let payload = { ...journal };
            // Ensure derived fields are set
            payload.total_debit = totals.debit;
            payload.total_credit = totals.credit;

            if (asPost) {
                payload = JournalEntryService.postJournal(payload);
            }

            if (payload.id) {
                await api.updateItem(payload.id, payload);
            } else {
                const newId = await api.addItem(payload);
                payload.id = newId;
            }

            setJournal(payload);
            setStatusMessage({ type: 'success', text: asPost ? 'Journal Posted Successfully!' : 'Journal Saved as Draft.' });

            if (!id && !asPost) {
                // If it was new and just saved draft, maybe replace URL to edit mode? 
                // navigate(`/apps/general-ledger/journals/${payload.id}`);
            }

        } catch (error) {
            console.error('Save failed', error);
            setStatusMessage({ type: 'error', text: 'Failed to save journal.' });
        } finally {
            setLoading(false);
        }
    };

    const handleWorkflowAction = async (action) => {
        setLoading(true);
        try {
            const api = helpersWrapper('journal_entries');
            let updatedJournal = { ...journal };

            if (action === 'submit') {
                updatedJournal = JournalEntryService.submitJournal(updatedJournal);
            } else if (action === 'approve') {
                updatedJournal = JournalEntryService.approveJournal(updatedJournal);
            } else if (action === 'reject') {
                const reason = window.prompt("Reason for rejection:"); // Simple prompt for now
                if (!reason) {
                    setLoading(false);
                    return; // Cancel
                }
                updatedJournal = JournalEntryService.rejectJournal(updatedJournal, reason);
            }

            await api.updateItem(updatedJournal.id, updatedJournal);
            setJournal(updatedJournal);
            setStatusMessage({ type: 'info', text: `Journal ${action}ed successfully.` });

        } catch (error) {
            console.error(`Failed to ${action}`, error);
            setStatusMessage({ type: 'error', text: `Failed to ${action} journal.` });
        } finally {
            setLoading(false);
        }
    };



    const handleReverse = async () => {
        if (!window.confirm("Are you sure you want to reverse this journal? A new draft journal will be created.")) return;

        setLoading(true);
        try {
            const api = helpersWrapper('journal_entries');
            const reversalDraft = JournalEntryService.reverseJournal(journal);
            const newId = await api.addItem(reversalDraft);

            // Navigate to the new reversal journal
            navigate(`/apps/general-ledger/journals/edit/${newId}`);

            // Ideally we'd also reload to show the new data, but navigation should trigger re-mount or id change effect
        } catch (error) {
            console.error("Failed to create reversal", error);
            setStatusMessage({ type: 'error', text: "Failed to create reversal entry." });
            setLoading(false);
        }
    };



    const handleGenerateRecurring = async () => {
        setLoading(true);
        try {
            const api = helpersWrapper('journal_entries');
            // Ensure we use the service to get the next occurrence object
            const nextOccurrence = JournalEntryService.generateNextOccurrence(journal);
            const newId = await api.addItem(nextOccurrence);

            setStatusMessage({ type: 'success', text: "Next occurrence generated successfully." });

            // Navigate to the new journal
            // setTimeout to allow toast to be seen? No, just go.
            navigate(`/apps/general-ledger/journals/edit/${newId}`);
        } catch (error) {
            console.error("Failed to generate recurrence", error);
            setStatusMessage({ type: 'error', text: "Failed to generate next occurrence." });
            setLoading(false);
        }
    };

    return (
        <Box p={3} pb={10}>
            {/* Header Section */}
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                <Box display="flex" alignItems="center">
                    <IconButton onClick={() => navigate(-1)} sx={{ mr: 1 }}>
                        <ArrowBackIcon />
                    </IconButton>
                    <Typography variant="h4" fontWeight="bold">
                        {journal.id ? `Edit Journal ${journal.journal_number}` : 'New Journal Entry'}
                    </Typography>
                </Box>
                <Box>
                    {/* DRAFT STATE ACTIONS */}
                    {(journal.status === 'draft' || journal.status === 'rejected') && (
                        <>
                            <Button
                                variant="outlined"
                                color="info"
                                onClick={handleValidate}
                                sx={{ mr: 1 }}
                            >
                                Validate
                            </Button>
                            <Button
                                variant="outlined"
                                startIcon={<SaveIcon />}
                                onClick={() => handleSave(false)}
                                sx={{ mr: 1 }}
                            >
                                Save Draft
                            </Button>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => handleWorkflowAction('submit')}
                                disabled={!isBalanced}
                                sx={{ mr: 1 }}
                            >
                                Submit
                            </Button>
                        </>
                    )}

                    {/* SUBMITTED STATE ACTIONS (Approver View) */}
                    {journal.status === 'submitted' && (
                        <>
                            <Button
                                variant="outlined"
                                color="error"
                                onClick={() => handleWorkflowAction('reject')}
                                sx={{ mr: 1 }}
                            >
                                Reject
                            </Button>
                            <Button
                                variant="contained"
                                color="success"
                                onClick={() => handleWorkflowAction('approve')}
                                sx={{ mr: 1 }}
                            >
                                Approve
                            </Button>
                        </>
                    )}

                    {/* APPROVED STATE ACTIONS */}
                    {journal.status === 'approved' && (
                        <Button
                            variant="contained"
                            color="success"
                            startIcon={<PostIcon />}
                            onClick={() => handleSave(true)} // Calls post
                        >
                            Post Journal
                        </Button>
                    )}

                    {/* POSTED STATE INDICATOR & ACTIONS */}
                    {journal.status === 'posted' && (
                        <Box display="flex" align="center">
                            <Typography variant="overline" sx={{ px: 2, border: '1px solid', borderColor: 'success.main', borderRadius: 1, color: 'success.main', fontWeight: 'bold', mr: 2 }}>
                                POSTED
                            </Typography>
                            <Button
                                variant="outlined"
                                color="warning"
                                onClick={handleReverse}
                            >
                                Reverse Journal
                            </Button>
                        </Box>
                    )}
                </Box>
            </Box>

            {statusMessage && (
                <Alert severity={statusMessage.type} onClose={() => setStatusMessage(null)} sx={{ mb: 2 }}>
                    {statusMessage.text}
                </Alert>
            )}

            <Tabs value={tabValue} onChange={(e, v) => setTabValue(v)} sx={{ mb: 3 }}>
                <Tab label="Manual Entry" />
                <Tab label="Recurrence / Schedule" />
                <Tab label="Audit & History" />
            </Tabs>

            {/* Journal Header Form */}
            <Paper sx={{ p: 3, mb: 3 }}>
                <Grid container spacing={3}>
                    {/* Row 1: ID, Dates */}
                    <Grid item xs={12} md={3}>
                        <TextField
                            label="Journal Number"
                            value={journal.journal_number}
                            fullWidth
                            disabled
                            variant="filled"
                        />
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            type="date"
                            label="Posting Date"
                            value={journal.posting_date}
                            onChange={(e) => handleHeaderChange('posting_date', e.target.value)}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                            disabled={journal.status === 'posted'}
                        />
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            type="date"
                            label="Document Date"
                            value={journal.document_date}
                            onChange={(e) => handleHeaderChange('document_date', e.target.value)}
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                            disabled={journal.status === 'posted'}
                        />
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            select
                            label="Currency"
                            value={journal.currency_code}
                            onChange={(e) => handleHeaderChange('currency_code', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                        >
                            {CURRENCIES.map(c => <MenuItem key={c} value={c}>{c}</MenuItem>)}
                        </TextField>
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            type="number"
                            label="Exchange Rate"
                            value={journal.exchange_rate}
                            onChange={(e) => handleHeaderChange('exchange_rate', parseFloat(e.target.value))}
                            fullWidth
                            disabled={journal.currency_code === 'USD' || journal.status === 'posted'}
                            inputProps={{ step: "0.0001" }}
                        />
                    </Grid>

                    {/* Row 2: Classification */}
                    <Grid item xs={12} md={3}>
                        <TextField
                            select
                            label="Journal Source"
                            value={journal.journal_source || 'manual'}
                            onChange={(e) => handleHeaderChange('journal_source', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                        >
                            <MenuItem value="manual">Manual</MenuItem>
                            <MenuItem value="accrual">Accrual</MenuItem>
                            <MenuItem value="adjustment">Adjustment</MenuItem>
                            <MenuItem value="system">System Generated</MenuItem>
                        </TextField>
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            select
                            label="Category"
                            value={journal.journal_category || 'general'}
                            onChange={(e) => handleHeaderChange('journal_category', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                        >
                            <MenuItem value="general">General</MenuItem>
                            <MenuItem value="reclass">Reclassification</MenuItem>
                            <MenuItem value="closing">Closing</MenuItem>
                            <MenuItem value="opening">Opening</MenuItem>
                        </TextField>
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            label="Legal Entity"
                            value={journal.legal_entity_id || ''}
                            onChange={(e) => handleHeaderChange('legal_entity_id', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                            helperText="Mock: Enter Entity ID"
                        />
                    </Grid>
                    <Grid item xs={12} md={3}>
                        <TextField
                            label="Accounting Period"
                            value={journal.accounting_period || ''}
                            onChange={(e) => handleHeaderChange('accounting_period', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                            placeholder="YYYY-MM"
                        />
                    </Grid>

                    {/* Row 3: Description & Ref */}
                    <Grid item xs={12} md={6}>
                        <TextField
                            label="Reference / Document #"
                            value={journal.reference_number}
                            onChange={(e) => handleHeaderChange('reference_number', e.target.value)}
                            fullWidth
                            disabled={journal.status === 'posted'}
                        />
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <TextField
                            label="Description"
                            value={journal.description}
                            onChange={(e) => handleHeaderChange('description', e.target.value)}
                            fullWidth
                            multiline
                            rows={1}
                            disabled={journal.status === 'posted'}
                        />
                    </Grid>
                </Grid>
            </Paper>

            {/* TAB CONTENT: MANUAL ENTRY (0) */}
            {
                tabValue === 0 && (
                    <Paper sx={{ mb: 3, overflow: 'hidden' }}>
                        <TableContainer sx={{ maxHeight: 600 }}>
                            <Table stickyHeader size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell width="20%">Account</TableCell>
                                        <TableCell width="25%">Description</TableCell>
                                        <TableCell width="12%" align="right">Debit</TableCell>
                                        <TableCell width="12%" align="right">Credit</TableCell>
                                        <TableCell width="10%">Cost Center</TableCell>
                                        <TableCell width="10%">Project</TableCell>
                                        <TableCell width="5%">Actions</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {(journal.lines || []).map((line, index) => (
                                        <TableRow key={index}>
                                            <TableCell>
                                                <TextField
                                                    select
                                                    value={line.account_id}
                                                    onChange={(e) => handleLineChange(index, 'account_id', e.target.value)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    disabled={journal.status === 'posted'}
                                                >
                                                    {ACCOUNTS.map(acc => (
                                                        <MenuItem key={acc.id} value={acc.id}>
                                                            {acc.code} - {acc.name}
                                                        </MenuItem>
                                                    ))}
                                                </TextField>
                                            </TableCell>
                                            <TableCell>
                                                <TextField
                                                    value={line.description}
                                                    onChange={(e) => handleLineChange(index, 'description', e.target.value)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    disabled={journal.status === 'posted'}
                                                />
                                            </TableCell>
                                            <TableCell align="right">
                                                <TextField
                                                    type="number"
                                                    value={line.debit}
                                                    onChange={(e) => handleLineChange(index, 'debit', parseFloat(e.target.value) || 0)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    inputProps={{ style: { textAlign: 'right' }, min: 0 }}
                                                    disabled={journal.status === 'posted'}
                                                />
                                            </TableCell>
                                            <TableCell align="right">
                                                <TextField
                                                    type="number"
                                                    value={line.credit}
                                                    onChange={(e) => handleLineChange(index, 'credit', parseFloat(e.target.value) || 0)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    inputProps={{ style: { textAlign: 'right' }, min: 0 }}
                                                    disabled={journal.status === 'posted'}
                                                />
                                            </TableCell>
                                            <TableCell>
                                                <TextField
                                                    value={line.cost_center}
                                                    onChange={(e) => handleLineChange(index, 'cost_center', e.target.value)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    disabled={journal.status === 'posted'}
                                                />
                                            </TableCell>
                                            <TableCell>
                                                <TextField
                                                    value={line.project_id}
                                                    onChange={(e) => handleLineChange(index, 'project_id', e.target.value)}
                                                    fullWidth
                                                    size="small"
                                                    variant="standard"
                                                    disabled={journal.status === 'posted'}
                                                />
                                            </TableCell>
                                            <TableCell>
                                                <IconButton
                                                    size="small"
                                                    color="error"
                                                    onClick={() => removeLine(index)}
                                                    disabled={journal.status === 'posted'}
                                                >
                                                    <DeleteIcon />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>

                        <Box p={2} borderTop="1px solid #eee">
                            <Button startIcon={<AddIcon />} onClick={addLine} disabled={journal.status === 'posted'}>
                                Add Line
                            </Button>
                        </Box>
                    </Paper>
                )
            }

            {/* TAB CONTENT: RECURRENCE (1) */}
            {
                tabValue === 1 && (
                    <Paper sx={{ p: 3, mb: 3 }}>
                        <Typography variant="h6" gutterBottom>Recurrence Settings</Typography>
                        <Grid container spacing={3}>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    select
                                    label="Recurrence Frequency"
                                    value={journal.recurrence_frequency || 'monthly'}
                                    onChange={(e) => handleHeaderChange('recurrence_frequency', e.target.value)}
                                    fullWidth
                                    disabled={journal.status === 'posted'}
                                >
                                    <MenuItem value="monthly">Monthly</MenuItem>
                                    <MenuItem value="quarterly">Quarterly</MenuItem>
                                    <MenuItem value="yearly">Yearly</MenuItem>
                                </TextField>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <TextField
                                    type="date"
                                    label="Next Run Date"
                                    value={journal.next_run_date || ''}
                                    onChange={(e) => handleHeaderChange('next_run_date', e.target.value)}
                                    fullWidth
                                    InputLabelProps={{ shrink: true }}
                                    disabled={journal.status === 'posted'}
                                />
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Box display="flex" alignItems="center" height="100%">
                                    <Typography variant="body2" color="textSecondary">
                                        Enabling recurrence will create a template from this journal.
                                    </Typography>
                                </Box>
                            </Grid>

                            {/* RECURRENCE ACTIONS */}
                            <Grid item xs={12}>
                                <Button
                                    variant="contained"
                                    color="secondary"
                                    disabled={!journal.next_run_date}
                                    onClick={handleGenerateRecurring}
                                >
                                    Generate Next Occurrence
                                </Button>
                            </Grid>
                        </Grid>
                    </Paper >
                )
            }

            {/* TAB CONTENT: AUDIT (2) */}
            {
                tabValue === 2 && (
                    <Paper sx={{ p: 3, mb: 3 }}>
                        <Typography variant="h6" gutterBottom>Audit Trail & History</Typography>
                        <Grid container spacing={2}>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    label="Created By"
                                    value={journal.created_by || 'System User'}
                                    fullWidth
                                    disabled
                                />
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    label="Created At"
                                    value={journal.created_at || new Date().toISOString()}
                                    fullWidth
                                    disabled
                                />
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    label="Posted By"
                                    value={journal.posted_by || '-'}
                                    fullWidth
                                    disabled
                                />
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <TextField
                                    label="Posted At"
                                    value={journal.posted_at || '-'}
                                    fullWidth
                                    disabled
                                />
                            </Grid>

                        </Grid>

                        <Divider sx={{ my: 3 }} />

                        <Typography variant="subtitle1" gutterBottom>Change Log / History</Typography>
                        <TableContainer sx={{ maxHeight: 300 }}>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Date/Time</TableCell>
                                        <TableCell>User</TableCell>
                                        <TableCell>Action</TableCell>
                                        <TableCell>Details</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {/* Mock History Data */}
                                    <TableRow>
                                        <TableCell>{journal.created_at || new Date().toISOString()}</TableCell>
                                        <TableCell>{journal.created_by || 'System'}</TableCell>
                                        <TableCell>Created</TableCell>
                                        <TableCell>Journal entry created</TableCell>
                                    </TableRow>
                                    {journal.status === 'submitted' && (
                                        <TableRow>
                                            <TableCell>{journal.submitted_at}</TableCell>
                                            <TableCell>Current User</TableCell>
                                            <TableCell>Submitted</TableCell>
                                            <TableCell>Submitted for approval</TableCell>
                                        </TableRow>
                                    )}
                                    {journal.status === 'approved' && (
                                        <TableRow>
                                            <TableCell>{journal.approved_at}</TableCell>
                                            <TableCell>Approver</TableCell>
                                            <TableCell>Approved</TableCell>
                                            <TableCell>Approved for posting</TableCell>
                                        </TableRow>
                                    )}
                                    {journal.status === 'posted' && (
                                        <TableRow>
                                            <TableCell>{journal.posted_at}</TableCell>
                                            <TableCell>Controller</TableCell>
                                            <TableCell>Posted</TableCell>
                                            <TableCell>Posted to General Ledger</TableCell>
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </Paper>
                )
            }

            {/* Footer Totals (Floating) */}
            <Paper
                elevation={6}
                sx={{
                    position: 'fixed',
                    bottom: 0,
                    left: 240, // Adjust based on sidebar width approx
                    right: 0,
                    p: 2,
                    bgcolor: isBalanced ? '#f0fff4' : '#fff0f0',
                    borderTop: '1px solid',
                    borderColor: isBalanced ? 'success.light' : 'error.light',
                    zIndex: 1000
                }}
            >
                <Grid container alignItems="center">
                    <Grid item xs={6}>
                        <Typography variant="subtitle1" fontWeight="bold" color={isBalanced ? 'success.main' : 'error.main'}>
                            {isBalanced ? 'Journal Balanced' : 'Journal Out of Balance'}
                        </Typography>
                    </Grid>
                    <Grid item xs={6} display="flex" justifyContent="flex-end" gap={4}>
                        <Box textAlign="right">
                            <Typography variant="caption" display="block">Total Debit</Typography>
                            <Typography variant="h6">{totals.debit.toFixed(2)}</Typography>
                        </Box>
                        <Box textAlign="right">
                            <Typography variant="caption" display="block">Total Credit</Typography>
                            <Typography variant="h6">{totals.credit.toFixed(2)}</Typography>
                        </Box>
                        <Box textAlign="right">
                            <Typography variant="caption" display="block">Difference</Typography>
                            <Typography variant="h6" color={totals.difference !== 0 ? 'error.main' : 'text.primary'}>
                                {totals.difference.toFixed(2)}
                            </Typography>
                        </Box>
                    </Grid>
                </Grid>
            </Paper>
        </Box >
    );
};

export default JournalEntryManager;

// src/components/Management/FinancialManagement/GeneralLedger/JournalEntryService.js

/**
 * Service to handle business logic for Journal Entries.
 * In a real application, this would interact with a backend API.
 * Here, it encapsulates the logic for validation, balancing, and state transitions.
 */

export const JournalEntryService = {

    /**
     * Creates a blank journal entry structure
     */
    createEmptyJournal: () => ({
        id: null, // Will be assigned on save
        journal_number: 'DRAFT',
        posting_date: new Date().toISOString().slice(0, 10),
        document_date: new Date().toISOString().slice(0, 10),
        reference_number: '',
        description: '',
        currency_code: 'USD',
        exchange_rate: 1.0,
        status: 'draft', // draft, posted, reversed
        source: 'MANUAL',
        category: 'GENERAL',
        lines: [
            // Start with two empty lines for convenience
            { id: 1, account_id: '', description: '', debit: 0, credit: 0, cost_center: '', project_id: '', tax_code: '' },
            { id: 2, account_id: '', description: '', debit: 0, credit: 0, cost_center: '', project_id: '', tax_code: '' }
        ]
    }),

    /**
     * Validates the journal entry before posting
     * @param {Object} journal 
     * @returns {Object} { isValid: boolean, errors: Array<string> }
     */
    validateJournal: (journal) => {
        const errors = [];

        if (!journal.posting_date) errors.push('Posting Date is required.');
        if (!journal.currency_code) errors.push('Currency is required.');
        if (!journal.lines || journal.lines.length < 2) errors.push('Journal must have at least 2 lines.');

        let totalDebit = 0;
        let totalCredit = 0;
        let hasAccount = true;

        journal.lines.forEach((line, index) => {
            const debit = parseFloat(line.debit) || 0;
            const credit = parseFloat(line.credit) || 0;

            if (!line.account_id && (debit !== 0 || credit !== 0)) {
                errors.push(`Line ${index + 1}: Account is required.`);
                hasAccount = false;
            }

            // Mock Rule: Expense accounts (starting with 5) require a Cost Center
            // We assume account_id in this mock is the code, or distinct enough. 
            // In reality we'd look up the account object.
            // Let's assume the mock ACCOUNTS list has IDs matching codes '5000', '5010' etc.
            if (line.account_id && line.account_id.toString().startsWith('5') && !line.cost_center) {
                errors.push(`Line ${index + 1}: Cost Center is required for Expense accounts (5xxx).`);
            }

            if (debit < 0 || credit < 0) {
                errors.push(`Line ${index + 1}: Negative amounts are not allowed.`);
            }

            if (debit > 0 && credit > 0) {
                // Technically some systems allow this, but usually it's one or the other per line
                errors.push(`Line ${index + 1}: Cannot have both Debit and Credit amounts.`);
            }

            totalDebit += debit;
            totalCredit += credit;
        });

        // Round to 2 decimals to avoid floating point issues
        totalDebit = Math.round(totalDebit * 100) / 100;
        totalCredit = Math.round(totalCredit * 100) / 100;

        if (totalDebit !== totalCredit) {
            errors.push(`Journal is not balanced. Total Debit: ${totalDebit}, Total Credit: ${totalCredit}. Difference: ${(totalDebit - totalCredit).toFixed(2)}`);
        }

        if (totalDebit === 0 && totalCredit === 0) {
            errors.push('Journal cannot be empty (Total amount is 0).');
        }

        return {
            isValid: errors.length === 0,
            errors,
            totals: { debit: totalDebit, credit: totalCredit }
        };
    },

    /**
     * Simulates posting to check for period locks or other backend constraints
     * @param {Object} journal 
     */
    simulatePosting: async (journal) => {
        // Mock backend check
        return new Promise((resolve) => {
            setTimeout(() => {
                const results = {
                    success: true,
                    warnings: []
                };

                // Example mocked validation
                const postingYear = new Date(journal.posting_date).getFullYear();
                if (postingYear < 2024) {
                    results.success = false;
                    results.errors = ['Fiscal Year ' + postingYear + ' is closed.'];
                }

                resolve(results);
            }, 500);
        });
    },

    /**
     * Submits the journal for approval
     * @param {Object} journal 
     */
    submitJournal: (journal) => {
        return {
            ...journal,
            status: 'submitted',
            submitted_at: new Date().toISOString(),
            // submitted_by: 'Current User' 
        };
    },

    /**
     * Approves the journal
     * @param {Object} journal 
     */
    approveJournal: (journal) => {
        return {
            ...journal,
            status: 'approved',
            approved_at: new Date().toISOString(),
            // approved_by: 'Current User'
        };
    },

    /**
     * Rejects the journal
     * @param {Object} journal 
     * @param {string} reason
     */
    rejectJournal: (journal, reason) => {
        return {
            ...journal,
            status: 'rejected',
            rejection_reason: reason,
            rejected_at: new Date().toISOString(),
            // rejected_by: 'Current User'
        };
    },

    /**
     * Finalizes the journal, essentially "Posting" it.
     * @param {Object} journal 
     * @returns {Object} Posted journal
     */
    postJournal: (journal) => {
        // Enforce approval before posting (unless it's a legacy data fix or system journal)
        if (journal.status !== 'approved' && journal.status !== 'draft') { // Allow draft for now if testing, strictly should be 'approved'
            // For strict workflow: if (journal.status !== 'approved') throw new Error("Must be approved");
        }

        return {
            ...journal,
            status: 'posted',
            journal_number: journal.journal_number === 'DRAFT' ? 'JE-' + Math.floor(Math.random() * 100000) : journal.journal_number,
            posted_at: new Date().toISOString(),
            posted_by: 'Current User' // Should come from context
        };
    },

    /**
     * Creates a reversal entry for a posted journal
     * @param {Object} journal 
     * @returns {Object} New Reversal Draft Journal
     */
    reverseJournal: (journal) => {
        return {
            ...journal,
            id: null, // New entry
            journal_number: 'DRAFT',
            reference_number: `Reversal of ${journal.journal_number}`,
            description: `Reversal: ${journal.description}`,
            posting_date: new Date().toISOString().slice(0, 10), // Today
            document_date: new Date().toISOString().slice(0, 10),
            status: 'draft',
            is_reversal: true,
            original_journal_id: journal.id,
            lines: journal.lines.map(line => ({
                ...line,
                id: Date.now() + Math.random(), // New Line IDs
                debit: line.credit,  // Swap
                credit: line.debit   // Swap
            })),
            created_at: null,
            created_by: null,
            posted_at: null,
            posted_by: null,
            submitted_at: null,
            approved_at: null,
            rejected_at: null
        };
    },

    /**
     * Generates the next occurrence of a recurring journal
     * @param {Object} journal The template journal
     * @returns {Object} The new journal entry
     */
    generateNextOccurrence: (journal) => {
        const nextDate = new Date(journal.next_run_date || new Date());

        // Calculate new dates based on frequency
        let futureDate = new Date(nextDate);
        if (journal.recurrence_frequency === 'monthly') {
            futureDate.setMonth(futureDate.getMonth() + 1);
        } else if (journal.recurrence_frequency === 'quarterly') {
            futureDate.setMonth(futureDate.getMonth() + 3);
        } else if (journal.recurrence_frequency === 'yearly') {
            futureDate.setFullYear(futureDate.getFullYear() + 1);
        }

        return {
            ...journal,
            id: null,
            journal_number: 'DRAFT',
            reference_number: `Recurrence ${journal.recurrence_frequency} - ${nextDate.toISOString().slice(0, 7)}`,
            description: journal.description,
            posting_date: nextDate.toISOString().slice(0, 10),
            document_date: nextDate.toISOString().slice(0, 10),
            status: 'draft',
            is_recurring: false, // The generated instance is not itself recurring (usually)
            recurrence_frequency: null,
            next_run_date: null,
            source: 'RECURRING',
            original_journal_id: journal.id,
            lines: journal.lines.map(line => ({
                ...line,
                id: Date.now() + Math.random() // New Line IDs
            })),
            created_at: null,
            created_by: null,
            posted_at: null,
            created_by: null,
            posted_at: null,
            posted_by: null
        };
    },

    /**
     * Simulates fetching exchange rate from an external API
     * @param {string} currencyCode 
     * @returns {Promise<number>} Exchange rate relative to Base (USD)
     */
    getExchangeRate: async (currencyCode) => {
        // Mock rates
        const rates = {
            'USD': 1.0,
            'EUR': 0.92,
            'GBP': 0.79,
            'CAD': 1.36
        };
        return new Promise(resolve => {
            setTimeout(() => resolve(rates[currencyCode] || 1.0), 300);
        });
    }
};

import React, { useState } from 'react';
import {
    Box,
    Grid,
    Card,
    CardContent,
    Typography,
    Button,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    TableContainer,
    Paper,
    List,
    ListItem,
    ListItemButton,
    ListItemIcon,
    ListItemText,
    Divider,
    Breadcrumbs,
    Link,
    Alert
} from '@mui/material';
import {
    Assessment,
    ReceiptLong,
    PieChart,
    ChevronRight,
    ArrowBack
} from '@mui/icons-material';

const JournalReports = () => {
    const [activeReport, setActiveReport] = useState(null);
    const [drillLevel, setDrillLevel] = useState(0); // 0: Report, 1: Account, 2: Journal, 3: Line
    const [selectedItem, setSelectedItem] = useState(null);

    const reports = [
        { id: 'trial_balance', name: 'Trial Balance', icon: <Assessment /> },
        { id: 'journal_register', name: 'Journal Register', icon: <ReceiptLong /> },
        { id: 'account_analysis', name: 'Account Analysis', icon: <PieChart /> },
    ];

    // Mock Data
    const accounts = [
        { id: '1001', name: 'Cash', balance: 50000 },
        { id: '1100', name: 'Accounts Receivable', balance: 125000 },
        { id: '2000', name: 'Accounts Payable', balance: -45000 },
        { id: '4000', name: 'Sales', balance: -250000 },
        { id: '5000', name: 'Expenses', balance: 120000 },
    ];

    const journals = [
        { id: 'JE-001', date: '2023-01-15', desc: 'Monthly Rent', amount: 5000 },
        { id: 'JE-005', date: '2023-01-20', desc: 'Office Supplies', amount: 1500 },
        { id: 'JE-012', date: '2023-01-31', desc: 'Payroll Run', amount: 45000 },
    ];

    const handleReportSelect = (report) => {
        setActiveReport(report);
        setDrillLevel(0);
        setSelectedItem(null);
    };

    const handleDrillDown = (item, level) => {
        setSelectedItem(item);
        setDrillLevel(level);
    };

    const handleBack = () => {
        setDrillLevel(prev => prev > 0 ? prev - 1 : 0);
    };

    return (
        <Box p={3} height="100%" display="flex">
            {/* SIDEBAR */}
            <Paper sx={{ width: 280, flexShrink: 0, mr: 3 }}>
                <Box p={2}>
                    <Typography variant="h6" fontWeight="bold">Financial Reports</Typography>
                    <Typography variant="body2" color="textSecondary">Select a report to view</Typography>
                </Box>
                <Divider />
                <List>
                    {reports.map((report) => (
                        <ListItem key={report.id} disablePadding>
                            <ListItemButton
                                selected={activeReport?.id === report.id}
                                onClick={() => handleReportSelect(report)}
                            >
                                <ListItemIcon>{report.icon}</ListItemIcon>
                                <ListItemText primary={report.name} />
                                <ChevronRight color="action" />
                            </ListItemButton>
                        </ListItem>
                    ))}
                </List>
            </Paper>

            {/* MAIN CONTENT */}
            <Box flexGrow={1}>
                {activeReport ? (
                    <Paper sx={{ p: 3, height: '100%' }}>
                        {/* Breadcrumbs / Header */}
                        <Box mb={3} display="flex" alignItems="center">
                            {drillLevel > 0 && (
                                <Button startIcon={<ArrowBack />} onClick={handleBack} sx={{ mr: 2 }}>
                                    Back
                                </Button>
                            )}
                            <Box>
                                <Typography variant="h5">{activeReport.name}</Typography>
                                <Breadcrumbs aria-label="breadcrumb">
                                    <Link underline="hover" color="inherit" onClick={() => setDrillLevel(0)}>Summary</Link>
                                    {drillLevel >= 1 && <Typography color="text.primary">Account Detail</Typography>}
                                    {drillLevel >= 2 && <Typography color="text.primary">Journal Detail</Typography>}
                                </Breadcrumbs>
                            </Box>
                        </Box>

                        {/* REPORT CONTENT: LEVEL 0 (SUMMARY) */}
                        {drillLevel === 0 && (
                            <TableContainer>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Account ID</TableCell>
                                            <TableCell>Account Name</TableCell>
                                            <TableCell align="right">Balance</TableCell>
                                            <TableCell align="center">Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {accounts.map(acc => (
                                            <TableRow key={acc.id} hover>
                                                <TableCell>{acc.id}</TableCell>
                                                <TableCell>{acc.name}</TableCell>
                                                <TableCell align="right">${acc.balance.toLocaleString()}</TableCell>
                                                <TableCell align="center">
                                                    <Button size="small" onClick={() => handleDrillDown(acc, 1)}>
                                                        Analyze
                                                    </Button>
                                                </TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        )}

                        {/* REPORT CONTENT: LEVEL 1 (ACCOUNT TRANSACTIONS) */}
                        {drillLevel === 1 && selectedItem && (
                            <Box>
                                <Typography variant="subtitle1" gutterBottom>
                                    Transactions for {selectedItem.name} ({selectedItem.id})
                                </Typography>
                                <TableContainer>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>Journal ID</TableCell>
                                                <TableCell>Date</TableCell>
                                                <TableCell>Description</TableCell>
                                                <TableCell align="right">Amount</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {journals.map(j => (
                                                <TableRow key={j.id} hover onClick={() => handleDrillDown(j, 2)} sx={{ cursor: 'pointer' }}>
                                                    <TableCell color="primary">{j.id}</TableCell>
                                                    <TableCell>{j.date}</TableCell>
                                                    <TableCell>{j.desc}</TableCell>
                                                    <TableCell align="right">${j.amount.toLocaleString()}</TableCell>
                                                </TableRow>
                                            ))}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </Box>
                        )}

                        {/* REPORT CONTENT: LEVEL 2 (JOURNAL DETAIL) */}
                        {drillLevel === 2 && selectedItem && (
                            <Box>
                                <Alert severity="info">
                                    Drilled down to Journal Transaction <strong>{selectedItem.id}</strong>.
                                    Here you would see the specific debits and credits for this entry.
                                </Alert>
                            </Box>
                        )}
                    </Paper>
                ) : (
                    <Box display="flex" alignItems="center" justifyContent="center" height="100%">
                        <Typography variant="h6" color="textSecondary">
                            Select a report to generate preview.
                        </Typography>
                    </Box>
                )}
            </Box>
        </Box>
    );
};

export default JournalReports;

import React, { useState } from 'react';
import {
    Box,
    Button,
    Grid,
    Paper,
    Typography,
    Card,
