import React from 'react';
import { Box, Typography, Card, CardContent, Grid, Alert, AlertTitle } from '@mui/material';
import { NotificationImportant } from '@mui/icons-material';

const ExceptionAlerts = () => {
    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h5" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <NotificationImportant color="error" /> Exception Management
            </Typography>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <Alert severity="error">
                        <AlertTitle>High Stockout Risk</AlertTitle>
                        Product <strong>SKU-992</strong> is projected to stock out in <strong>3 days</strong> (Lead time: 14 days).
                    </Alert>
                </Grid>
                <Grid item xs={12}>
                    <Alert severity="warning">
                        <AlertTitle>Late Supplier Alert</AlertTitle>
                        PO #10234 from <strong>Vendor A</strong> is 2 days overdue. Impacting safety stock buffers.
                    </Alert>
                </Grid>
                <Grid item xs={12}>
                    <Alert severity="info">
                        <AlertTitle>Excess Inventory</AlertTitle>
                        Warehouse B holding <strong>200%</strong> of Max Level for SKU-112. Recommended transfer.
                    </Alert>
                </Grid>
            </Grid>
        </Box>
    );
};

export default ExceptionAlerts;

import React from 'react';
import { Box, Typography } from '@mui/material';
import {
    Treemap,
    ResponsiveContainer,
    Tooltip
} from 'recharts';

const data = [
    {
        name: 'Electronics',
        children: [
            { name: 'Laptops', size: 1200 },
            { name: 'Monitors', size: 800 },
            { name: 'Keyboards', size: 400 },
        ],
    },
    {
        name: 'Furniture',
        children: [
            { name: 'Desks', size: 2000 },
            { name: 'Chairs', size: 1500 },
            { name: 'Cabinets', size: 500 },
        ],
    },
    {
        name: 'Office Supplies',
        children: [
            { name: 'Paper', size: 300 },
            { name: 'Pens', size: 100 },
        ]
    }
];

const CustomContent = (props) => {
    const { root, depth, x, y, width, height, index, name } = props;

    return (
        <g>
            <rect
                x={x}
                y={y}
                width={width}
                height={height}
                style={{
                    fill: depth < 2 ? '#8884d8' : 'none',
                    stroke: '#fff',
                    strokeWidth: 2 / (depth + 1e-10),
                    strokeOpacity: 1 / (depth + 1e-10),
                }}
            />
            {depth === 1 ? (
                <text
                    x={x + width / 2}
                    y={y + height / 2 + 7}
                    textAnchor="middle"
                    fill="#fff"
                    fontSize={14}
                >
                    {name}
                </text>
            ) : null}
        </g>
    );
};

const InventoryHeatmap = () => {
    return (
        <Box sx={{ p: 2, height: 400 }}>
            <Typography variant="h6" gutterBottom>Inventory Value Distribution</Typography>
            <ResponsiveContainer width="100%" height="100%">
                <Treemap
                    data={data}
                    dataKey="size"
                    aspectRatio={4 / 3}
                    stroke="#fff"
                    fill="#8884d8"
                />
            </ResponsiveContainer>
        </Box>
    );
};

