import React from 'react';
import { ListItem, ListItemText, ListItemIcon, Box, Typography, IconButton, Chip } from '@mui/material';
import {
    Assignment,
    CheckCircle,
    Verified,
    Close,
    Circle
} from '@mui/icons-material';
import { format } from 'date-fns';
import { useNavigate } from 'react-router-dom';

const NotificationItem = ({ notification, onMarkAsRead, onDelete }) => {
    const navigate = useNavigate();

    const getIcon = () => {
        switch (notification.type) {
            case 'task_assigned':
                return <Assignment color="primary" />;
            case 'task_completed':
                return <CheckCircle color="success" />;
            case 'entity_validated':
                return <Verified color="info" />;
            default:
                return <Circle color="action" />;
        }
    };

    const getPriorityColor = () => {
        // You can extract priority from notification message or add it as a field
        return 'default';
    };

    const handleClick = () => {
        // Mark as read
        if (!notification.is_read) {
            onMarkAsRead(notification.id);
        }

        // Navigate to related entity or task
        if (notification.related_task_id) {
            navigate(`/project-task-management/tasks/tasks?view=${notification.related_task_id}`);
        } else if (notification.related_entity_type && notification.related_entity_id) {
            // Navigate to entity view
            // This will depend on your routing structure
            navigate(`/management/${notification.related_entity_type}?view=${notification.related_entity_id}`);
        }
    };

    return (
        <ListItem
            sx={{
                bgcolor: notification.is_read ? 'transparent' : 'action.hover',
                borderLeft: notification.is_read ? 'none' : '3px solid',
                borderColor: 'primary.main',
                cursor: 'pointer',
                '&:hover': {
                    bgcolor: 'action.selected'
                }
            }}
            secondaryAction={
                <IconButton edge="end" size="small" onClick={(e) => {
                    e.stopPropagation();
                    onDelete(notification.id);
                }}>
                    <Close fontSize="small" />
                </IconButton>
            }
            onClick={handleClick}
        >
            <ListItemIcon>
                {getIcon()}
            </ListItemIcon>
            <ListItemText
                primary={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="subtitle2" sx={{ fontWeight: notification.is_read ? 400 : 600 }}>
                            {notification.title}
                        </Typography>
                        {!notification.is_read && (
                            <Box
                                sx={{
                                    width: 8,
                                    height: 8,
                                    borderRadius: '50%',
                                    bgcolor: 'primary.main'
                                }}
                            />
                        )}
                    </Box>
                }
                secondary={
                    <Box sx={{ mt: 0.5 }}>
                        <Typography variant="body2" color="text.secondary">
                            {notification.message}
                        </Typography>
                        <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5, display: 'block' }}>
                            {format(new Date(notification.created_at), 'MMM d, yyyy h:mm a')}
                        </Typography>
                    </Box>
                }
            />
        </ListItem>
    );
};

export default NotificationItem;

import React, { useState, useEffect } from 'react';
import {
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Button,
    TextField,
    Autocomplete,
    MenuItem,
    Box,
    FormControlLabel,
    Checkbox,
    Alert
} from '@mui/material';
import { Send } from '@mui/icons-material';
import { helpersWrapper } from '../../utils/clientQueries';
import useNotifications from '../../hooks/useNotifications';
import { useAuthorization } from '../../hooks/useAuthorization';

const TaskAssignmentDialog = ({ open, onClose, entityType, entityId, entityName }) => {
    const { currentUser } = useAuthorization();
    const { createNotification } = useNotifications(currentUser?.id);

    const [users, setUsers] = useState([]);
    const [loadingUsers, setLoadingUsers] = useState(true);

    const [formData, setFormData] = useState({
        assignedTo: null,
        title: '',
        description: '',
        priority: 'medium',
        slaDeadline: '',
        sendEmail: true
    });

    const [saving, setSaving] = useState(false);
    const [error, setError] = useState(null);

    const taskHelper = helpersWrapper('tasks');
    const userHelper = helpersWrapper('users');

    // Load users
    useEffect(() => {
        const loadUsers = async () => {
            try {
                const allUsers = await userHelper.fetchItems();
                setUsers(allUsers);
                setLoadingUsers(false);
            } catch (err) {
                console.error('Failed to load users:', err);
                setLoadingUsers(false);
            }
        };

        if (open) {
            loadUsers();
            // Set default title based on entity
            if (entityType && entityName) {
                setFormData(prev => ({
                    ...prev,
                    title: `Review ${entityType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}: ${entityName}`
                }));
            }
        }
    }, [open, entityType, entityName]);

    const handleSubmit = async () => {
        if (!formData.assignedTo || !formData.title) {
            setError('Please select a user and provide a task title');
            return;
        }

        setSaving(true);
        setError(null);

        try {
            // Create task
            const newTask = {
                title: formData.title,
                description: formData.description,
                assigned_to: formData.assignedTo.id,
                assigned_by_user_id: currentUser.id,
                assigned_via: formData.assignedTo.email ? 'email' : 'user_id',
                related_entity_type: entityType,
                related_entity_id: entityId,
                priority: formData.priority,
                sla_deadline: formData.slaDeadline ? new Date(formData.slaDeadline).toISOString() : null,
                status: 'pending',
                notification_sent: true,
                email_notification_sent: formData.sendEmail,
                created_at: new Date().toISOString()
            };

            const createdTask = await taskHelper.addItem(newTask);

            // Create notification for assigned user
            await createNotification({
                userId: formData.assignedTo.id,
                type: 'task_assigned',
                title: 'New Task Assigned',
                message: `${currentUser.first_name} ${currentUser.last_name} assigned you: ${formData.title}`,
                relatedTaskId: createdTask.id,
                relatedEntityType: entityType,
                relatedEntityId: entityId
            });

            // TODO: Send email if requested (requires backend email service)
            if (formData.sendEmail) {
                console.log('Email notification would be sent to:', formData.assignedTo.email);
                // await emailService.sendTaskAssignment(formData.assignedTo.email, newTask);
            }

            // Reset form and close
            setFormData({
                assignedTo: null,
                title: '',
                description: '',
                priority: 'medium',
                slaDeadline: '',
                sendEmail: true
            });
            onClose();
        } catch (err) {
            console.error('Failed to create task:', err);
            setError('Failed to create task. Please try again.');
        } finally {
            setSaving(false);
        }
    };

    return (
        <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
            <DialogTitle>Assign Task</DialogTitle>

            <DialogContent>
                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
                    {/* Assign To User */}
                    <Autocomplete
                        options={users}
                        getOptionLabel={(option) => `${option.first_name} ${option.last_name} (${option.email})`}
                        loading={loadingUsers}
                        value={formData.assignedTo}
                        onChange={(e, value) => setFormData(prev => ({ ...prev, assignedTo: value }))}
                        renderInput={(params) => (
                            <TextField
                                {...params}
                                label="Assign To"
                                required
                                helperText="Search by name or email"
                            />
                        )}
                    />

                    {/* Task Title */}
                    <TextField
                        label="Task Title"
                        required
                        fullWidth
                        value={formData.title}
                        onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                    />

                    {/* Description */}
                    <TextField
                        label="Description"
                        multiline
                        rows={3}
                        fullWidth
                        value={formData.description}
                        onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                    />

                    {/* Priority */}
                    <TextField
                        select
                        label="Priority"
                        value={formData.priority}
                        onChange={(e) => setFormData(prev => ({ ...prev, priority: e.target.value }))}
                        fullWidth
                    >
                        <MenuItem value="low">Low</MenuItem>
                        <MenuItem value="medium">Medium</MenuItem>
                        <MenuItem value="high">High</MenuItem>
                        <MenuItem value="urgent">Urgent</MenuItem>
                    </TextField>

                    {/* SLA Deadline */}
                    <TextField
                        label="SLA Deadline"
                        type="datetime-local"
                        fullWidth
                        value={formData.slaDeadline}
                        onChange={(e) => setFormData(prev => ({ ...prev, slaDeadline: e.target.value }))}
                        InputLabelProps={{ shrink: true }}
                        helperText="Optional deadline for task completion"
                    />

                    {/* Send Email */}
                    <FormControlLabel
                        control={
                            <Checkbox
                                checked={formData.sendEmail}
                                onChange={(e) => setFormData(prev => ({ ...prev, sendEmail: e.target.checked }))}
                            />
                        }
                        label="Send email notification"
                    />
                </Box>
            </DialogContent>

            <DialogActions>
                <Button onClick={onClose} disabled={saving}>
                    Cancel
                </Button>
                <Button
                    variant="contained"
                    onClick={handleSubmit}
                    disabled={saving || !formData.assignedTo || !formData.title}
                    startIcon={<Send />}
                >
                    {saving ? 'Assigning...' : 'Assign Task'}
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default TaskAssignmentDialog;

import React from 'react';
import { Box, Typography, IconButton, Tooltip, Paper } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

const TreasuryTotals = ({ rows = [], columns = [] }) => {
    // Calculate totals for numeric columns (excluding 'year')
    // Calculate totals for numeric columns (excluding 'year')
    const calculateTotals = (rows, type) => {
        return columns.reduce((acc, col) => {
            if (col.type === 'number' && col.field !== 'year') {
                acc[col.field] = rows.reduce((sum, row) => {
                    const val = row[col.field] || 0;
                    // Filter based on row's invoiceType
                    if (type === 'Asset' && row.invoiceType !== 'Asset') return sum;
                    if (type === 'Liability' && row.invoiceType !== 'Liability') return sum;
                    // For 'Net', include all
                    return sum + val;
                }, 0);
            } else {
                acc[col.field] = '';
            }
            return acc;
        }, {});
    };

    const assetTotals = calculateTotals(rows, 'Asset');
    const liabilityTotals = calculateTotals(rows, 'Liability');
    const netTotals = calculateTotals(rows, 'Net');

    // Set row labels (assuming first column is the label column)
    if (columns.length > 0) {
        assetTotals[columns[0].field] = 'Total Assets';
        liabilityTotals[columns[0].field] = 'Total Liabilities';
        netTotals[columns[0].field] = 'Net Total';
    }

    const handleCopyRow = (data, label) => {
        const headers = columns.map(c => c.headerName).join(',');
        const values = columns.map(c => {
            const val = data[c.field];
            return typeof val === 'number' ? val.toFixed(2) : val;
        }).join(',');
        const csv = `${headers}\n${values}`;
        navigator.clipboard.writeText(csv);
    };

    const handleCopyCell = (value) => {
        navigator.clipboard.writeText(value);
    };

    const renderRow = (data, label, color, copyTooltip) => (
        <Box sx={{ display: 'flex', minWidth: 'min-content', borderBottom: '1px solid', borderColor: 'divider' }}>
            {columns.map((col, index) => {
                const value = data[col.field];
                let displayValue = value;
                const isNumber = col.type === 'number' && typeof value === 'number' && col.field !== 'year';

                if (isNumber) {
                    if (col.valueFormatter) {
                        displayValue = col.valueFormatter(value);
                    } else {
                        displayValue = value.toLocaleString();
                    }
                }

                return (
                    <Box key={col.field} sx={{
                        width: col.width,
                        minWidth: col.width,
                        p: 1,
                        borderRight: '1px solid',
                        borderColor: 'divider',
                        fontWeight: 'bold',
                        color: color || 'inherit',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        bgcolor: 'action.hover'
                    }}>
                        <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                            {displayValue}
                        </Typography>
                        {/* Add copy row button to the first cell of the row only */}
                        {index === 0 && (
                            <Tooltip title={copyTooltip}>
                                <IconButton onClick={() => handleCopyRow(data, label)} size="small" sx={{ ml: 1 }}>
                                    <ContentCopyIcon fontSize="small" />
                                </IconButton>
                            </Tooltip>
                        )}
                    </Box>
                );
            })}
        </Box>
    );

    return (
        <Paper elevation={3} sx={{ mt: 2, p: 2, overflowX: 'auto' }}>
            <Box sx={{ mb: 1 }}>
                <Typography variant="h6">Summary Totals</Typography>
            </Box>

            {/* Header / Column alignment is handled by fixed widths matching DataGrid */}

            {renderRow(assetTotals, 'Total Assets', 'success.main', 'Copy Assets Row')}
            {renderRow(liabilityTotals, 'Total Liabilities', 'error.main', 'Copy Liabilities Row')}
            {renderRow(netTotals, 'Net Total', 'text.primary', 'Copy Net Row')}
        </Paper>
    );
};

export default TreasuryTotals;

import React, { useState, useEffect } from 'react';
import {
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Button,
    Typography,
    Box,
    Chip,
    IconButton,
    Tooltip,
    Alert,
    CircularProgress,
    List,
    ListItem,
    Divider
} from '@mui/material';
import {
    History,
    Restore,
    Person,
    CheckCircle,
    RadioButtonUnchecked,
    FiberManualRecord
} from '@mui/icons-material';
import { format } from 'date-fns';
import useVersioning from '../../hooks/useVersioning';
import VersionComparison from './VersionComparison';
import { useAuthorization } from '../../hooks/useAuthorization';

const VersionHistoryDialog = ({ open, onClose, entityType, rowId, onRestore }) => {
    const { currentUser } = useAuthorization();
    const { versionHistory, loading, fetchVersionHistory, restoreVersion, compareVersions } = useVersioning(entityType);

    const [selectedVersions, setSelectedVersions] = useState([]);
    const [comparisonOpen, setComparisonOpen] = useState(false);
    const [restoring, setRestoring] = useState(false);

    useEffect(() => {
        if (open && rowId) {
            fetchVersionHistory(rowId);
        }
    }, [open, rowId, fetchVersionHistory]);

    const handleRestore = async (version) => {
        if (!window.confirm(`Restore to version ${version.version_number}? This will create a new version with the old data.`)) {
            return;
        }

        setRestoring(true);
        try {
            await restoreVersion(version, currentUser?.id);

            if (onRestore) {
                onRestore();
            }

            await fetchVersionHistory(rowId);
        } catch (error) {
            alert('Failed to restore version: ' + error.message);
        } finally {
            setRestoring(false);
        }
    };

    const handleCompare = () => {
        if (selectedVersions.length === 2) {
            setComparisonOpen(true);
        }
    };

    const toggleVersionSelection = (version) => {
        setSelectedVersions(prev => {
            const isSelected = prev.find(v => v.id === version.id);
            if (isSelected) {
                return prev.filter(v => v.id !== version.id);
            } else if (prev.length < 2) {
                return [...prev, version];
            }
            return prev;
        });
    };

    return (
        <>
            <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
                <DialogTitle>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <History />
                        <Typography variant="h6">Version History</Typography>
                    </Box>
                </DialogTitle>

                <DialogContent>
                    {loading ? (
                        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                            <CircularProgress />
                        </Box>
                    ) : versionHistory.length === 0 ? (
                        <Alert severity="info">No version history available</Alert>
                    ) : (
                        <>
                            {selectedVersions.length === 2 && (
                                <Alert
                                    severity="info"
                                    sx={{ mb: 2 }}
                                    action={
                                        <Button size="small" onClick={handleCompare}>
                                            Compare
                                        </Button>
                                    }
                                >
                                    2 versions selected for comparison
                                </Alert>
                            )}

                            <List>
                                {versionHistory.map((version, index) => (
                                    <React.Fragment key={version.id}>
                                        <ListItem
                                            sx={{
                                                flexDirection: 'column',
                                                alignItems: 'flex-start',
                                                py: 2,
                                                bgcolor: version.is_current_version ? 'action.hover' : 'transparent'
                                            }}
                                        >
                                            <Box sx={{ display: 'flex', width: '100%', gap: 2 }}>
                                                {/* Version indicator dot */}
                                                <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minWidth: 40 }}>
                                                    <IconButton
                                                        size="small"
                                                        onClick={() => toggleVersionSelection(version)}
                                                        color={version.is_current_version ? 'primary' : 'default'}
                                                    >
                                                        {selectedVersions.find(v => v.id === version.id) ? (
                                                            <CheckCircle />
                                                        ) : (
                                                            <FiberManualRecord fontSize="small" />
                                                        )}
                                                    </IconButton>
                                                    {index < versionHistory.length - 1 && (
                                                        <Box sx={{ width: 2, height: '100%', minHeight: 30, bgcolor: 'divider', mt: 1 }} />
                                                    )}
                                                </Box>

                                                {/* Version info */}
                                                <Box sx={{ flex: 1 }}>
                                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
                                                        <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                                                            Version {version.version_number}
                                                        </Typography>
                                                        {version.is_current_version && (
                                                            <Chip label="Current" size="small" color="primary" />
                                                        )}
                                                    </Box>

                                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mb: 0.5 }}>
                                                        <Person fontSize="small" color="action" />
                                                        <Typography variant="body2" color="text.secondary">
                                                            Modified by User {version.version_author_id || 'Unknown'}
                                                        </Typography>
                                                    </Box>

                                                    <Typography variant="caption" color="text.secondary">
                                                        {format(new Date(version.version_date), 'MMM d, yyyy h:mm a')}
                                                    </Typography>

                                                    {!version.is_current_version && (
                                                        <Box sx={{ mt: 1 }}>
                                                            <Tooltip title="Restore this version">
                                                                <Button
                                                                    size="small"
                                                                    startIcon={<Restore />}
                                                                    onClick={() => handleRestore(version)}
                                                                    disabled={restoring}
                                                                    variant="outlined"
                                                                >
                                                                    Restore
                                                                </Button>
                                                            </Tooltip>
                                                        </Box>
                                                    )}
                                                </Box>
                                            </Box>
                                        </ListItem>
                                        {index < versionHistory.length - 1 && <Divider />}
                                    </React.Fragment>
                                ))}
                            </List>
                        </>
                    )}
                </DialogContent>

                <DialogActions>
                    <Button onClick={onClose}>Close</Button>
                </DialogActions>
            </Dialog>

            {/* Version Comparison Dialog */}
            {selectedVersions.length === 2 && (
                <VersionComparison
                    open={comparisonOpen}
                    onClose={() => setComparisonOpen(false)}
                    version1={selectedVersions[0]}
                    version2={selectedVersions[1]}
                    compareVersions={compareVersions}
                />
            )}
        </>
    );
};

export default VersionHistoryDialog;

import React, { useState, useEffect } from 'react';
import {
    Box,
    Paper,
    Typography,
    Button,
    IconButton,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    CircularProgress,
    Alert,
    Tooltip
} from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import { Add, Delete, Edit, Save, Cancel } from '@mui/icons-material';
import { helpersWrapper } from '../utils/clientQueries';

const WorkflowRulesEditor = ({ entityName }) => {
    const [rules, setRules] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [editDialogOpen, setEditDialogOpen] = useState(false);
    const [currentRule, setCurrentRule] = useState(null);
    const [selectedRows, setSelectedRows] = useState([]);

    const rulesHelper = helpersWrapper('entity_workflow_rules');

    // Fetch rules for selected entity
    useEffect(() => {
        if (entityName) {
            fetchRules();
        }
    }, [entityName]);

    const fetchRules = async () => {
        setLoading(true);
        setError(null);
        try {
            const allRules = await rulesHelper.fetchItems();
            const filtered = allRules.filter(rule => rule.target_entity === entityName);
            setRules(filtered);
        } catch (err) {
            setError('Failed to load workflow rules: ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleAddRule = () => {
        setCurrentRule({
            target_entity: entityName,
            step_name: '',
            step_label: '',
            actions: '[]',
            next_step: '',
            icon: '',
            type: 'primary'
        });
        setEditDialogOpen(true);
    };

    const handleEditRule = (rule) => {
        setCurrentRule({ ...rule });
        setEditDialogOpen(true);
    };

    const handleSaveRule = async () => {
        try {
            if (currentRule.id) {
                await rulesHelper.updateItem(currentRule.id, currentRule);
            } else {
                await rulesHelper.addItem(currentRule);
            }
            setEditDialogOpen(false);
            fetchRules();
        } catch (err) {
            setError('Failed to save rule: ' + err.message);
        }
    };

    const handleDeleteRules = async () => {
        if (!window.confirm(`Delete ${selectedRows.length} rule(s)?`)) return;

        try {
            for (const id of selectedRows) {
                await rulesHelper.deleteItem(id);
            }
            setSelectedRows([]);
            fetchRules();
        } catch (err) {
            setError('Failed to delete rules: ' + err.message);
        }
    };

    const columns = [
        { field: 'id', headerName: 'ID', width: 80 },
        { field: 'target_entity', headerName: 'Entity', width: 200 },
        { field: 'step_name', headerName: 'Step Name', width: 150, editable: false },
        { field: 'step_label', headerName: 'Label', width: 150 },
        { field: 'actions', headerName: 'Actions (JSON)', width: 200, flex: 1 },
        { field: 'next_step', headerName: 'Next Step', width: 150 },
        { field: 'icon', headerName: 'Icon', width: 100 },
        { field: 'type', headerName: 'Type', width: 100 },
        {
            field: 'edit',
            headerName: 'Edit',
            width: 80,
            sortable: false,
            renderCell: (params) => (
                <IconButton size="small" onClick={() => handleEditRule(params.row)}>
                    <Edit fontSize="small" />
                </IconButton>
            )
        }
    ];

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Box>
            {error && (
                <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
                    {error}
                </Alert>
            )}

            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                <Typography variant="h6">
                    Workflow Rules for: {entityName}
                </Typography>
                <Box>
                    {selectedRows.length > 0 && (
                        <Button
                            variant="outlined"
                            color="error"
                            startIcon={<Delete />}
                            onClick={handleDeleteRules}
                            sx={{ mr: 1 }}
                        >
                            Delete ({selectedRows.length})
                        </Button>
                    )}
                    <Button
                        variant="contained"
                        startIcon={<Add />}
                        onClick={handleAddRule}
                    >
                        Add Rule
                    </Button>
                </Box>
            </Box>

            <Box sx={{ height: 500, width: '100%' }}>
                <DataGrid
                    rows={rules}
                    columns={columns}
                    checkboxSelection
                    disableRowSelectionOnClick
                    onRowSelectionModelChange={(newSelection) => {
                        setSelectedRows(newSelection);
                    }}
                    pageSizeOptions={[10, 25, 50]}
                    initialState={{
                        pagination: { paginationModel: { pageSize: 10 } }
                    }}
                />
            </Box>

            {/* Edit Dialog */}
            <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)} maxWidth="md" fullWidth>
                <DialogTitle>
                    {currentRule?.id ? 'Edit Workflow Rule' : 'Add Workflow Rule'}
                </DialogTitle>
                <DialogContent>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
                        <TextField
                            label="Step Name"
                            value={currentRule?.step_name || ''}
                            onChange={(e) => setCurrentRule({ ...currentRule, step_name: e.target.value })}
                            fullWidth
                        />
                        <TextField
                            label="Step Label"
                            value={currentRule?.step_label || ''}
                            onChange={(e) => setCurrentRule({ ...currentRule, step_label: e.target.value })}
                            fullWidth
                        />
                        <TextField
                            label="Actions (JSON Array)"
                            value={currentRule?.actions || '[]'}
                            onChange={(e) => setCurrentRule({ ...currentRule, actions: e.target.value })}
                            multiline
                            rows={4}
                            fullWidth
                            helperText='e.g., ["submit", "approve", "reject"]'
                        />
                        <TextField
                            label="Next Step"
                            value={currentRule?.next_step || ''}
                            onChange={(e) => setCurrentRule({ ...currentRule, next_step: e.target.value })}
                            fullWidth
                        />
                        <TextField
                            label="Icon"
                            value={currentRule?.icon || ''}
                            onChange={(e) => setCurrentRule({ ...currentRule, icon: e.target.value })}
                            fullWidth
                        />
                        <TextField
                            label="Type"
                            value={currentRule?.type || 'primary'}
                            onChange={(e) => setCurrentRule({ ...currentRule, type: e.target.value })}
                            fullWidth
                        />
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setEditDialogOpen(false)} startIcon={<Cancel />}>
                        Cancel
                    </Button>
                    <Button onClick={handleSaveRule} variant="contained" startIcon={<Save />}>
                        Save
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default WorkflowRulesEditor;

