import { useState, useEffect, useCallback } from 'react';
import { helpersWrapper } from '../utils/clientQueries';

const useNotifications = (userId) => {
    const [notifications, setNotifications] = useState([]);
    const [unreadCount, setUnreadCount] = useState(0);
    const [loading, setLoading] = useState(true);

    const notificationHelper = helpersWrapper('notifications');

    // Fetch notifications
    const fetchNotifications = useCallback(async () => {
        if (!userId) return;

        try {
            const allNotifications = await notificationHelper.fetchItems();
            const userNotifications = allNotifications
                .filter(n => n.user_id === userId)
                .sort((a, b) => new Date(b.created_at) - new Date(a.created_at));

            setNotifications(userNotifications);
            setUnreadCount(userNotifications.filter(n => !n.is_read).length);
            setLoading(false);
        } catch (error) {
            console.error('Failed to fetch notifications:', error);
            setLoading(false);
        }
    }, [userId]);

    // Initial load
    useEffect(() => {
        fetchNotifications();
    }, [fetchNotifications]);

    // Poll for new notifications every 30 seconds
    useEffect(() => {
        const interval = setInterval(fetchNotifications, 30000);
        return () => clearInterval(interval);
    }, [fetchNotifications]);

    // Mark notification as read
    const markAsRead = useCallback(async (notificationId) => {
        try {
            await notificationHelper.updateItem(notificationId, {
                is_read: true,
                read_at: new Date().toISOString()
            });

            setNotifications(prev => prev.map(n =>
                n.id === notificationId ? { ...n, is_read: true, read_at: new Date().toISOString() } : n
            ));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (error) {
            console.error('Failed to mark notification as read:', error);
        }
    }, []);

    // Mark all as read
    const markAllAsRead = useCallback(async () => {
        try {
            const unreadNotifications = notifications.filter(n => !n.is_read);

            await Promise.all(
                unreadNotifications.map(n =>
                    notificationHelper.updateItem(n.id, {
                        is_read: true,
                        read_at: new Date().toISOString()
                    })
                )
            );

            setNotifications(prev => prev.map(n => ({ ...n, is_read: true, read_at: new Date().toISOString() })));
            setUnreadCount(0);
        } catch (error) {
            console.error('Failed to mark all as read:', error);
        }
    }, [notifications]);

    // Create notification
    const createNotification = useCallback(async (notification) => {
        try {
            const newNotification = {
                user_id: notification.userId,
                type: notification.type,
                title: notification.title,
                message: notification.message,
                related_task_id: notification.relatedTaskId || null,
                related_entity_type: notification.relatedEntityType || null,
                related_entity_id: notification.relatedEntityId || null,
                is_read: false,
                created_at: new Date().toISOString()
            };

            await notificationHelper.addItem(newNotification);

            // Refresh notifications
            fetchNotifications();
        } catch (error) {
            console.error('Failed to create notification:', error);
            throw error;
        }
    }, [fetchNotifications]);

    // Delete notification
    const deleteNotification = useCallback(async (notificationId) => {
        try {
            await notificationHelper.deleteItem(notificationId);
            setNotifications(prev => prev.filter(n => n.id !== notificationId));

            const deletedNotification = notifications.find(n => n.id === notificationId);
            if (deletedNotification && !deletedNotification.is_read) {
                setUnreadCount(prev => Math.max(0, prev - 1));
            }
        } catch (error) {
            console.error('Failed to delete notification:', error);
        }
    }, [notifications]);

    return {
        notifications,
        unreadCount,
        loading,
        markAsRead,
        markAllAsRead,
        createNotification,
        deleteNotification,
        refresh: fetchNotifications
    };
};

export default useNotifications;

import { useState, useCallback } from 'react';
import { helpersWrapper } from '../utils/clientQueries';

const useVersioning = (entityType) => {
    const [versionHistory, setVersionHistory] = useState([]);
    const [loading, setLoading] = useState(false);

    const helper = helpersWrapper(entityType);

    // Fetch version history for a specific original_row_id
    const fetchVersionHistory = useCallback(async (originalRowId) => {
        setLoading(true);
        try {
            const allRows = await helper.fetchItems();

            // Get all versions of this row
            const versions = allRows
                .filter(row => row.original_row_id === originalRowId || row.id === originalRowId)
                .sort((a, b) => b.version_number - a.version_number); // Latest first

            setVersionHistory(versions);
            return versions;
        } catch (error) {
            console.error('Failed to fetch version history:', error);
            throw error;
        } finally {
            setLoading(false);
        }
    }, [entityType]);

    // Create a new version (called during update)
    const createNewVersion = useCallback(async (currentRow, updatedData, userId) => {
        try {
            // Determine the original_row_id
            const originalRowId = currentRow.original_row_id || currentRow.id;

            // Create new version
            const newVersion = {
                ...updatedData,
                version_number: currentRow.version_number + 1,
                version_date: new Date().toISOString(),
                version_author_id: userId,
                is_current_version: true,
                is_versioned: true,
                original_row_id: originalRowId,
                created_at: new Date().toISOString()
            };

            // Mark current version as not current
            if (currentRow.id) {
                await helper.updateItem(currentRow.id, {
                    is_current_version: false
                });
            }

            // Add new version
            const createdVersion = await helper.addItem(newVersion);

            return createdVersion;
        } catch (error) {
            console.error('Failed to create new version:', error);
            throw error;
        }
    }, [entityType]);

    // Restore a previous version (creates new version with old data)
    const restoreVersion = useCallback(async (versionToRestore, userId) => {
        try {
            // Get the current version
            const allRows = await helper.fetchItems();
            const currentVersion = allRows.find(
                row => row.is_current_version &&
                    (row.original_row_id === versionToRestore.original_row_id ||
                        row.id === versionToRestore.original_row_id)
            );

            if (!currentVersion) {
                throw new Error('Current version not found');
            }

            // Create a copy of the version to restore, but as a new version
            const { id, created_at, updated_at, version_number, version_date, version_author_id, is_current_version, ...dataToRestore } = versionToRestore;

            const restoredVersion = await createNewVersion(currentVersion, dataToRestore, userId);

            return restoredVersion;
        } catch (error) {
            console.error('Failed to restore version:', error);
            throw error;
        }
    }, [entityType, createNewVersion]);

    // Compare two versions
    const compareVersions = useCallback((version1, version2) => {
        const changes = [];
        const allKeys = new Set([...Object.keys(version1), ...Object.keys(version2)]);

        // Exclude version-related fields from comparison
        const excludeFields = [
            'id', 'version_number', 'version_date', 'version_author_id',
            'is_current_version', 'original_row_id', 'created_at', 'updated_at'
        ];

        allKeys.forEach(key => {
            if (excludeFields.includes(key)) return;

            const val1 = version1[key];
            const val2 = version2[key];

            if (JSON.stringify(val1) !== JSON.stringify(val2)) {
                changes.push({
                    field: key,
                    oldValue: val1,
                    newValue: val2
                });
            }
        });

        return changes;
    }, []);

    // Soft delete for versioned rows
    const softDelete = useCallback(async (rowId, userId) => {
        try {
            const row = await helper.fetchItem(rowId);

            if (!row.is_versioned) {
                // Not versioned, perform regular delete
                await helper.deleteItem(rowId);
                return;
            }

            // Mark as not current (soft delete)
            await helper.updateItem(rowId, {
                is_current_version: false,
                deleted_at: new Date().toISOString(),
                deleted_by: userId
            });
        } catch (error) {
            console.error('Failed to soft delete:', error);
            throw error;
        }
    }, [entityType]);

    return {
        versionHistory,
        loading,
        fetchVersionHistory,
        createNewVersion,
        restoreVersion,
        compareVersions,
        softDelete
    };
};

export default useVersioning;

// src/layout/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  CssBaseline,
  IconButton,
  Button,
  Container,
  Typography,
} from '@mui/material';
import { styled, useTheme } from '@mui/material/styles';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

import { translate } from '../utils/translate';
import { useTranslation } from '../contexts/TranslationProvider';
import { usePageDescription } from '../contexts/PageDescriptionContext';
import { useNavigate, useLocation } from 'react-router-dom';

import DrawerDashboard from './Drawer';
import { keyToLinkMap } from './keyToLinkMap';
import { generateRandomDataAsync } from '../utils/dataGenerator';
import GenerationProgressModal from '../components/GenerationProgressModal';
import NotificationBar from '../components/Notifications/NotificationBar';

const expandedDrawerWidth = 300;
const collapsedDrawerWidth = 70;

// count /a/b/c format routes only
const isThreeSegmentRoute = (route) =>
  route.split('/').filter(Boolean).length === 3;

const validRoutes = Object.values(keyToLinkMap).filter(isThreeSegmentRoute);

const Main = styled('main', {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  marginTop: 50,
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create(['margin', 'width'], {
    duration: theme.transitions.duration.standard,
  }),
  marginLeft: open ? expandedDrawerWidth : collapsedDrawerWidth,
  [theme.breakpoints.down('md')]: {
    // still align with collapsed/expanded drawer, but smaller screens
    marginLeft: open ? expandedDrawerWidth : collapsedDrawerWidth,
    padding: theme.spacing(2),
  },
}));

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    duration: theme.transitions.duration.standard,
  }),
  zIndex: theme.zIndex.drawer + 1,
  ...(open && {
    width: `calc(100% - ${expandedDrawerWidth}px)`,
    marginLeft: `${expandedDrawerWidth}px`,
  }),
  ...(!open && {
    width: `calc(100% - ${collapsedDrawerWidth}px)`,
    marginLeft: `${collapsedDrawerWidth}px`,
  }),
}));

const Dashboard = ({ children, colorMode }) => {
  const theme = useTheme();
  const { language, toggleLanguage, t } = useTranslation();
  const { description } = usePageDescription();
  const navigate = useNavigate();
  const location = useLocation();

  const [open, setOpen] = useState(false);
  const [currentAnalysisPage, setCurrentAnalysisPage] = useState(0);

  // Generation Modal State
  const [genModalOpen, setGenModalOpen] = useState(false);
  const [genProgressLog, setGenProgressLog] = useState([]);
  const [genIsFinished, setGenIsFinished] = useState(false);

  const [showAnalytics, setShowAnalytics] = useState(() => {
    const saved = localStorage.getItem('showAnalytics');
    return saved !== null ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    localStorage.setItem('showAnalytics', JSON.stringify(showAnalytics));
  }, [showAnalytics]);

  const handleDrawerToggle = () => setOpen((prev) => !prev);

  // simple logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userData');
    localStorage.removeItem('organizationData');
    window.location.href = '/';
  };

  const toggleShowAnalytics = () => {
    setShowAnalytics((prev) => !prev);
    setCurrentAnalysisPage(0);
  };

  const handleNextAnalysis = () => {
    const index = validRoutes.indexOf(location.pathname);
    if (index !== -1) {
      navigate(validRoutes[(index + 1) % validRoutes.length]);
    }
  };

  const handlePreviousAnalysis = () => {
    const index = validRoutes.indexOf(location.pathname);
    if (index !== -1) {
      navigate(
        validRoutes[(index - 1 + validRoutes.length) % validRoutes.length]
      );
    }
  };

  const handleGenerateData = async () => {
    setGenModalOpen(true);
    setGenProgressLog([]);
    setGenIsFinished(false);

    await generateRandomDataAsync((log) => {
      setGenProgressLog(prev => [...prev, log]);
    });

    setGenIsFinished(true);
  };

  const handleCloseGenModal = () => {
    setGenModalOpen(false);
  };

  // keyboard shortcuts
  useEffect(() => {
    const handler = (event) => {
      if (event.shiftKey) {
        if (event.key === 'ArrowLeft') handlePreviousAnalysis();
        if (event.key === 'ArrowRight') handleNextAnalysis();
      } else if (event.ctrlKey && event.key === 'Enter') {
        navigate('/summary');
      }
    };

    window.addEventListener('keydown', handler);
    return () => window.removeEventListener('keydown', handler);
  }, [location.pathname, navigate]);

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <GenerationProgressModal
        open={genModalOpen}
        onClose={handleCloseGenModal}
        isFinished={genIsFinished}
        progressLog={genProgressLog}
      />

      <AppBar position="fixed" open={open}>
        <Toolbar
          sx={{
            minHeight: 56,
            display: 'flex',
            justifyContent: 'space-between',
          }}
        >
          {/* Drawer toggle on left */}
          <IconButton
            color="inherit"
            onClick={handleDrawerToggle}
            edge="start"
            sx={{ mr: 2 }}
          >
            {open ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>

          {/* Right side controls */}
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'flex-end',
              alignItems: 'center',
              gap: 1,
              flexWrap: 'nowrap',
              overflowX: 'auto',
            }}
          >
            <Button
              variant="outlined"
              color="inherit"
              size="small"
              onClick={toggleShowAnalytics}
              sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
            >
              {showAnalytics
                ? translate('Show Data', language)
                : translate('Show Analysis', language)}
            </Button>

            {showAnalytics && (
              <>
                <Button
                  variant="outlined"
                  color="inherit"
                  size="small"
                  sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
                  onClick={handlePreviousAnalysis}
                >
                  Previous
                </Button>

                <Button
                  variant="outlined"
                  color="inherit"
                  size="small"
                  sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
                  onClick={handleNextAnalysis}
                >
                  Next
                </Button>
              </>
            )}

            <Button
              variant="outlined"
              color="inherit"
              size="small"
              sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
              onClick={toggleLanguage}
            >
              {translate('Switch Language', language)}
            </Button>

            <Button
              variant="outlined"
              color="inherit"
              size="small"
              sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
              onClick={colorMode.toggleColorMode}
            >
              {colorMode.mode === 'dark' ? 'Light' : 'Dark'}
            </Button>

            <Button
              variant="outlined"
              color="inherit"
              size="small"
              sx={{ whiteSpace: 'nowrap', flexShrink: 0 }}
              onClick={handleLogout}
            >
              {translate('Logout', language)}
            </Button>

            <NotificationBar />

            <Button
              variant="contained"
              color="secondary"
              size="small"
              sx={{ mr: 1, whiteSpace: 'nowrap', flexShrink: 0 }}
              onClick={handleGenerateData}
            >
              Generate Random Row
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <DrawerDashboard
        open={open}
        onToggleDrawer={handleDrawerToggle}
      />

      <Main open={open}>
        <Box mb={2}>
          <Box sx={{ p: 2, bgcolor: 'background.paper', borderRadius: 1, boxShadow: 1 }}>
            <Typography variant="body2" color="textSecondary">
              {description}
            </Typography>
          </Box>
        </Box>
        <Box>
          {React.cloneElement(children, {
            showAnalytics,
            currentAnalysisPage,
          })}
        </Box>
      </Main>
    </Box >
  );
};

export default Dashboard;

