          {!open && (
            <Box
              sx={{
                flexBasis: '100%',
                maxWidth: '100%',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <ListItemButton
                component={Link}
                to="/summary"
                disableGutters
                sx={{
                  width: 44,
                  height: 44,
                  borderRadius: '0px', // Sharp
                  justifyContent: 'center',
                  '&:hover': {
                    backgroundColor: 'rgba(0,0,0,0.04)',
                  },
                }}
              >
                <ListItemIcon sx={{ minWidth: 0, color: '#000000' }}>
                  <span style={{ fontSize: 22 }}>🔥</span>
                </ListItemIcon>
              </ListItemButton>
            </Box>
          )}

        </Box>

        {/* Navigation area */}
        <StyledFireNav>
          {/* Collapsed: icons-only list */}
          {!open && (
            <FireNav component="nav" disablePadding>
              {drawerItems.map(({ key, label, children }) => {
                const TopIcon = moduleToIconMap[key];
                let targetPath = '#';
                if (
                  Array.isArray(children) &&
                  children.length === 1 &&
                  !children[0].children
                ) {
                  const leafKey = children[0].key;
                  targetPath = keyToLinkMap[leafKey] || '#';
                }

                const ButtonComponent = targetPath !== '#' ? Link : 'button';

                return (
                  <React.Fragment key={key}>
                    <Tooltip title={t(label)} placement="right">
                      <ListItemButton
                        component={ButtonComponent}
                        {...(ButtonComponent === Link ? { to: targetPath } : {})}
                        sx={{
                          justifyContent: 'center',
                          py: 1.5,
                          '&:hover': { bgcolor: 'rgba(0,0,0,0.04)' }
                        }}
                      >
                        <ListItemIcon
                          sx={{
                            minWidth: 0,
                            justifyContent: 'center',
                            color: '#000000'
                          }}
                        >
                          {TopIcon ? <TopIcon /> : <ChevronRightIcon />}
                        </ListItemIcon>
                      </ListItemButton>
                    </Tooltip>
                    {/* Divider after major category */}
                    <Box sx={{ height: '1px', bgcolor: '#000000', width: '100%' }} />
                  </React.Fragment>
                );
              })}
            </FireNav>
          )}

          {/* Expanded: full nested structure with labels */}
          {open && (
            <FireNav component="nav" disablePadding sx={{ pt: 0 }}>
              {drawerItems.map(
                ({ key, label, description, children }) => {
                  const TopIcon = moduleToIconMap[key];

                  return (
                    <React.Fragment key={key}>
                      <ListItemButton onClick={() => handleClick(key)} sx={{ '&:hover': { bgcolor: 'rgba(0,0,0,0.04)' } }}>
                        <ListItemIcon sx={{ color: '#000000' }}>
                          {TopIcon ? <TopIcon /> : <ChevronRightIcon />}
                        </ListItemIcon>
                        <ListItemText
                          primary={t(label)}
                          primaryTypographyProps={{
                            fontSize: 15,
                            fontWeight: 'medium',
                            lineHeight: '20px',
                            color: '#000000'
                          }}
                        />
                        {children ? (
                          openItems[key] ? <ExpandLess sx={{ color: '#000000' }} /> : <ExpandMore sx={{ color: '#000000' }} />
                        ) : null}
                      </ListItemButton>

                      {/* FULL WIDTH DIVIDER */}
                      <Box sx={{ height: '1px', bgcolor: '#000000', width: '100%' }} />

                      {children && (
                        <Collapse
                          in={openItems[key]}
                          timeout="auto"
                          unmountOnExit
                        >
                          <List component="div" disablePadding sx={{ bgcolor: '#FAFAFA' }}>
                            {children.map(
                              ({
                                key: subKey,
                                label: subLabel,
                                children: subChildren,
                              }) => (
                                <React.Fragment key={subKey}>
                                  <ListItemButton
                                    onClick={() => handleClick(subKey)}
                                    sx={{ '&:hover': { bgcolor: 'rgba(0,0,0,0.04)' } }}
                                  >
                                    <ListItemText
                                      sx={{ pl: 4 }}
                                      primary={`• ${t(subLabel)}`}
                                      primaryTypographyProps={{ color: '#000000' }}
                                    />
                                    {subChildren ? (
                                      openItems[subKey] ? (
                                        <ExpandLess sx={{ color: '#000000' }} />
                                      ) : (
                                        <ExpandMore sx={{ color: '#000000' }} />
                                      )
                                    ) : (
                                      <ChevronRightIcon sx={{ color: '#000000' }} />
                                    )}
                                  </ListItemButton>

                                  {subChildren && (
                                    <Collapse
                                      in={openItems[subKey]}
                                      timeout="auto"
                                      unmountOnExit
                                    >
                                      <List
                                        component="div"
                                        disablePadding
                                      >
                                        {subChildren.map(
                                          ({
                                            key: subSubKey,
                                            label: subSubLabel,
                                            description: subDesc,
                                          }) => (
                                            <Link
                                              to={
                                                keyToLinkMap[subSubKey] ||
                                                '#'
                                              }
                                              key={subSubKey}
                                              style={{
                                                textDecoration: 'none',
                                                color: 'inherit',
                                              }}
                                            >
                                              <ListItemButton sx={{ '&:hover': { bgcolor: 'rgba(0,0,0,0.04)' } }}>
                                                <Tooltip
                                                  title={
                                                    t(subDesc ||
                                                      description ||
                                                      '')
                                                  }
                                                  placement="right"
                                                >
                                                  <ListItemText
                                                    sx={{ pl: 6 }}
                                                    primary={`• ${t(subSubLabel)}`}
                                                    primaryTypographyProps={{ color: '#444' }}
                                                  />
                                                </Tooltip>
                                              </ListItemButton>
                                            </Link>
                                          )
                                        )}
                                      </List>
                                    </Collapse>
                                  )}
                                </React.Fragment>
                              )
                            )}
                          </List>
                        </Collapse>
                      )}
                    </React.Fragment>
                  );
                }
              )}
            </FireNav>
          )}
        </StyledFireNav>
      </Paper>
    </Box>
  );
}

