import React, { useState, useEffect } from 'react';
import {
    Container,
    Card,
    CardContent,
    Typography,
    Button,
    Box,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Chip,
    IconButton,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Alert,
    Paper,
    Stack,
    Grid,
    Tooltip
} from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import {
    Add,
    Edit,
    Delete,
    PlayArrow,
    Visibility,
    CheckCircle,
    Calculate,
    Warning,
    AutoFixHigh
} from '@mui/icons-material';
import api from '../utils/apiClient';
import { configRegistry } from '../components/Management/configRegistry';
import RuleEngine from '../utils/RuleEngine';

const RULE_TYPES = [
    { value: 'visibility', label: 'Visibility', icon: <Visibility />, color: 'primary' },
    { value: 'required', label: 'Required', icon: <CheckCircle />, color: 'error' },
    { value: 'calculation', label: 'Calculation', icon: <Calculate />, color: 'success' },
    { value: 'validation', label: 'Validation', icon: <Warning />, color: 'warning' },
    { value: 'auto_fill', label: 'Auto-Fill', icon: <AutoFixHigh />, color: 'info' }
];

const BusinessRulesManager = () => {
    const [entityType, setEntityType] = useState('');
    const [rules, setRules] = useState([]);
    const [loading, setLoading] = useState(false);
    const [dialogOpen, setDialogOpen] = useState(false);
    const [editingRule, setEditingRule] = useState(null);
    const [testDialogOpen, setTestDialogOpen] = useState(false);
    const [testData, setTestData] = useState({});
    const [testResult, setTestResult] = useState(null);

    const availableEntities = Object.keys(configRegistry);

    useEffect(() => {
        if (entityType) {
            fetchRules();
        }
    }, [entityType]);

    const fetchRules = async () => {
        setLoading(true);
        try {
            const { data } = await api.get('/business-rules', {
                params: { entityType }
            });
            setRules(data || []);
        } catch (err) {
            console.error('Failed to fetch rules:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleCreate = () => {
        setEditingRule({
            entity_type: entityType,
            rule_name: '',
            rule_type: 'visibility',
            target_field: '',
            condition_logic: { operator: 'equals', field: '', value: '' },
            action_config: {},
            priority: 0,
            is_active: true,
            description: ''
        });
        setDialogOpen(true);
    };

    const handleEdit = (rule) => {
        setEditingRule({
            ...rule,
            condition_logic: typeof rule.condition_logic === 'string' ?
                JSON.parse(rule.condition_logic) : rule.condition_logic,
            action_config: typeof rule.action_config === 'string' ?
                JSON.parse(rule.action_config) : rule.action_config
        });
        setDialogOpen(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Are you sure you want to delete this rule?')) return;

        try {
            await api.delete(`/business-rules/${id}`);
            fetchRules();
        } catch (err) {
            console.error('Failed to delete rule:', err);
            alert('Failed to delete rule');
        }
    };

    const handleSave = async () => {
        try {
            if (editingRule.id) {
                await api.put(`/business-rules/${editingRule.id}`, editingRule);
            } else {
                await api.post('/business-rules', editingRule);
            }
            setDialogOpen(false);
            setEditingRule(null);
            fetchRules();
        } catch (err) {
            console.error('Failed to save rule:', err);
            alert('Failed to save rule');
        }
    };

    const handleTest = (rule) => {
        setEditingRule(rule);
        setTestData({});
        setTestResult(null);
        setTestDialogOpen(true);
    };

    const runTest = () => {
        try {
            const result = RuleEngine.applyRules([editingRule], testData);
            setTestResult(result);
        } catch (err) {
            console.error('Test failed:', err);
            setTestResult({ error: err.message });
        }
    };

    const getRuleTypeInfo = (type) => {
        return RULE_TYPES.find(rt => rt.value === type) || RULE_TYPES[0];
    };

    const columns = [
        {
            field: 'rule_type',
            headerName: 'Type',
            width: 130,
            renderCell: (params) => {
                const typeInfo = getRuleTypeInfo(params.value);
                return (
                    <Chip
                        icon={typeInfo.icon}
                        label={typeInfo.label}
                        size="small"
                        color={typeInfo.color}
                    />
                );
            }
        },
        {
            field: 'rule_name',
            headerName: 'Rule Name',
            flex: 1,
            minWidth: 200
        },
        {
            field: 'target_field',
            headerName: 'Target Field',
            width: 150
        },
        {
            field: 'priority',
            headerName: 'Priority',
            width: 90,
            align: 'center'
        },
        {
            field: 'is_active',
            headerName: 'Active',
            width: 90,
            renderCell: (params) => (
                <Chip
                    label={params.value ? 'Active' : 'Inactive'}
                    size="small"
                    color={params.value ? 'success' : 'default'}
                />
            )
        },
        {
            field: 'actions',
            headerName: 'Actions',
            width: 150,
            renderCell: (params) => (
                <Box>
                    <Tooltip title="Test Rule">
                        <IconButton size="small" onClick={() => handleTest(params.row)} color="primary">
                            <PlayArrow fontSize="small" />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Edit">
                        <IconButton size="small" onClick={() => handleEdit(params.row)} color="primary">
                            <Edit fontSize="small" />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Delete">
                        <IconButton size="small" onClick={() => handleDelete(params.row.id)} color="error">
                            <Delete fontSize="small" />
                        </IconButton>
                    </Tooltip>
                </Box>
            ),
            sortable: false,
            filterable: false
        }
    ];

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            <Typography variant="h4" gutterBottom sx={{ fontWeight: 700, mb: 4 }}>
                Business Rules Manager
            </Typography>

            <Alert severity="info" sx={{ mb: 3 }}>
                Configure dynamic business logic for entities without code changes. Rules control field visibility,
                required status, calculations, validations, and auto-fill behavior.
            </Alert>

            <Card sx={{ mb: 3 }}>
                <CardContent>
                    <Grid container spacing={2} alignItems="center">
                        <Grid item xs={12} md={8}>
                            <FormControl fullWidth>
                                <InputLabel>Select Entity</InputLabel>
                                <Select
                                    value={entityType}
                                    onChange={(e) => setEntityType(e.target.value)}
                                    label="Select Entity"
                                >
                                    {availableEntities.map(entity => (
                                        <MenuItem key={entity} value={entity}>
                                            {entity.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item xs={12} md={4}>
                            <Button
                                variant="contained"
                                startIcon={<Add />}
                                onClick={handleCreate}
                                disabled={!entityType}
                                fullWidth
                            >
                                Create Rule
                            </Button>
                        </Grid>
                    </Grid>
                </CardContent>
            </Card>

            {entityType && (
                <Card>
                    <CardContent>
                        <Typography variant="h6" gutterBottom>
                            Rules for {entityType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                        </Typography>

                        <DataGrid
                            rows={rules}
                            columns={columns}
                            pageSize={10}
                            rowsPerPageOptions={[10, 25, 50]}
                            autoHeight
                            loading={loading}
                            disableSelectionOnClick
                            sx={{ mt: 2 }}
                        />
                    </CardContent>
                </Card>
            )}

            {/* Rule Editor Dialog */}
            <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)} maxWidth="md" fullWidth>
                <DialogTitle>
                    {editingRule?.id ? 'Edit Rule' : 'Create Rule'}
                </DialogTitle>
                <DialogContent>
                    {editingRule && (
                        <Stack spacing={2} sx={{ mt: 1 }}>
                            <TextField
                                fullWidth
                                label="Rule Name"
                                value={editingRule.rule_name}
                                onChange={(e) => setEditingRule({ ...editingRule, rule_name: e.target.value })}
                            />

                            <FormControl fullWidth>
                                <InputLabel>Rule Type</InputLabel>
                                <Select
                                    value={editingRule.rule_type}
                                    onChange={(e) => setEditingRule({ ...editingRule, rule_type: e.target.value })}
                                    label="Rule Type"
                                >
                                    {RULE_TYPES.map(type => (
                                        <MenuItem key={type.value} value={type.value}>
                                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                                {type.icon}
                                                {type.label}
                                            </Box>
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>

                            <TextField
                                fullWidth
                                label="Target Field"
                                value={editingRule.target_field}
                                onChange={(e) => setEditingRule({ ...editingRule, target_field: e.target.value })}
                            />

                            <TextField
                                fullWidth
                                label="Condition Logic (JSON)"
                                multiline
                                rows={4}
                                value={JSON.stringify(editingRule.condition_logic, null, 2)}
                                onChange={(e) => {
                                    try {
                                        const parsed = JSON.parse(e.target.value);
                                        setEditingRule({ ...editingRule, condition_logic: parsed });
                                    } catch (err) {
                                        // Invalid JSON, ignore
                                    }
                                }}
                            />

                            <TextField
                                fullWidth
                                label="Action Configuration (JSON)"
                                multiline
                                rows={4}
                                value={JSON.stringify(editingRule.action_config, null, 2)}
                                onChange={(e) => {
                                    try {
                                        const parsed = JSON.parse(e.target.value);
                                        setEditingRule({ ...editingRule, action_config: parsed });
                                    } catch (err) {
                                        // Invalid JSON, ignore
                                    }
                                }}
                            />

                            <Grid container spacing={2}>
                                <Grid item xs={6}>
                                    <TextField
                                        fullWidth
                                        type="number"
                                        label="Priority"
                                        value={editingRule.priority}
                                        onChange={(e) => setEditingRule({ ...editingRule, priority: parseInt(e.target.value) })}
                                    />
                                </Grid>
                                <Grid item xs={6}>
                                    <FormControl fullWidth>
                                        <InputLabel>Status</InputLabel>
                                        <Select
                                            value={editingRule.is_active}
                                            onChange={(e) => setEditingRule({ ...editingRule, is_active: e.target.value })}
                                            label="Status"
                                        >
                                            <MenuItem value={true}>Active</MenuItem>
                                            <MenuItem value={false}>Inactive</MenuItem>
                                        </Select>
                                    </FormControl>
                                </Grid>
                            </Grid>

                            <TextField
                                fullWidth
                                label="Description (optional)"
                                multiline
                                rows={2}
                                value={editingRule.description || ''}
                                onChange={(e) => setEditingRule({ ...editingRule, description: e.target.value })}
                            />
                        </Stack>
                    )}
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setDialogOpen(false)}>Cancel</Button>
                    <Button onClick={handleSave} variant="contained">Save</Button>
                </DialogActions>
            </Dialog>

            {/* Test Dialog */}
            <Dialog open={testDialogOpen} onClose={() => setTestDialogOpen(false)} maxWidth="sm" fullWidth>
                <DialogTitle>Test Rule</DialogTitle>
                <DialogContent>
                    <Stack spacing={2} sx={{ mt: 1 }}>
                        <Alert severity="info">
                            Enter test data as JSON to see how the rule evaluates
                        </Alert>

                        <TextField
                            fullWidth
                            label="Test Data (JSON)"
                            multiline
                            rows={6}
                            value={JSON.stringify(testData, null, 2)}
                            onChange={(e) => {
                                try {
                                    const parsed = JSON.parse(e.target.value);
                                    setTestData(parsed);
                                } catch (err) {
                                    // Invalid JSON
                                }
                            }}
                            placeholder='{"status": "paid", "amount": 5000}'
                        />

                        {testResult && (
                            <Paper variant="outlined" sx={{ p: 2 }}>
                                <Typography variant="subtitle2" gutterBottom>
                                    Result:
                                </Typography>
                                <pre style={{ fontSize: '12px', overflow: 'auto' }}>
                                    {JSON.stringify(testResult, null, 2)}
                                </pre>
                            </Paper>
                        )}
                    </Stack>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setTestDialogOpen(false)}>Close</Button>
                    <Button onClick={runTest} variant="contained" startIcon={<PlayArrow />}>
                        Run Test
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
};

export default BusinessRulesManager;

import React, { useEffect, useState, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Grid, Avatar } from '@mui/material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip as RechartsTooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';
import { helpersWrapper } from '../utils/clientQueries';
import { collectionName as logsCollectionName } from '../components/Management/manager_action_logs';
import { collectionName as commentsCollectionName } from '../components/Management/entity_comments_history';
import { AccessTime, Person, CheckCircleOutline, InsertComment, ArrowForward } from '@mui/icons-material';
import { getConfig } from '../components/Management/configRegistry';

// --- HELPERS ---
const pathify = (urlParam) => {
    if (!urlParam) return '';
    return urlParam
        .split('-')
        .map((s) => s.charAt(0).toUpperCase() + s.slice(1))
        .join('');
};

const formatDate = (val) => {
    if (!val) return '—';
    try {
        return new Date(val).toLocaleString(undefined, {
            year: 'numeric', month: 'short', day: 'numeric',
            hour: '2-digit', minute: '2-digit'
        });
    } catch {
        return val;
    }
};

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d', '#ff7675', '#74b9ff'];

// --- STYLES ---
const styles = {
    // A4 Container
    a4Page: {
        width: '210mm',
        height: '297mm',
        padding: '20mm', // Standard Margin
        margin: '0 auto',
        backgroundColor: 'white',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)', // Screen shadow
        marginBottom: '20px',
        position: 'relative',
        overflow: 'hidden', // Strict constraint
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column'
    },
    headerStrip: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '8px',
        background: 'linear-gradient(90deg, #1976d2 0%, #64b5f6 100%)',
    },
    sectionTitle: {
        fontWeight: 800,
        textTransform: 'uppercase',
        letterSpacing: '0.1em',
        color: '#2c3e50',
        marginBottom: '16px',
        borderBottom: '2px solid #eee',
        paddingBottom: '8px',
        marginTop: '10px'
    },
    label: {
        fontSize: '0.7rem',
        fontWeight: 700,
        color: '#7f8c8d',
        textTransform: 'uppercase',
        marginBottom: '2px'
    },
    value: {
        fontSize: '0.9rem',
        fontWeight: 500,
        color: '#2c3e50',
    },
    card: {
        padding: '15px',
        border: '1px solid #e0e0e0',
        borderRadius: '8px',
        marginBottom: '15px',
    },
    stepContainer: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginTop: '20px',
        marginBottom: '20px',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px'
    }
};

const ConfigurablePdf = () => {
    const { main, sub, entity, id } = useParams();
    const [loading, setLoading] = useState(true);
    const [data, setData] = useState(null);
    const [config, setConfig] = useState(null);
    const [logs, setLogs] = useState([]);
    const [comments, setComments] = useState([]);
    const [details, setDetails] = useState([]);
    const [detailConfig, setDetailConfig] = useState(null);
    const [stats, setStats] = useState([]);

    // Helpers
    const collectionName = entity;
    const queryHelpers = useMemo(() => helpersWrapper(collectionName), [collectionName]);
    const logsHelpers = useMemo(() => helpersWrapper(logsCollectionName), []);
    const commentsHelpers = useMemo(() => helpersWrapper(commentsCollectionName), []);

    // Load Data
    useEffect(() => {
        const loadAll = async () => {
            try {
                // 1. Load Config logic
                const componentName = pathify(entity);
                let loadedConfig = getConfig(componentName);

                // Fallback / Try finding it if name mismatch (e.g. subfolder prefix)
                if (!loadedConfig) {
                    // Try standard import just in case registry is outdated
                    try {
                        const holdingFolder = pathify(main);
                        const subFolder = pathify(sub);
                        const configModule = await import(`../components/Management/${holdingFolder}/${subFolder}/${componentName}`);
                        loadedConfig = configModule?.default || configModule;
                    } catch (e) { console.warn("Fallback import failed", e); }
                }
                setConfig(loadedConfig);

                // 2. Load Item Data
                const item = await queryHelpers.fetchItemById(id);
                setData(item);

                // 3. Load Logs (History)
                const allLogs = await logsHelpers.fetchItems();
                const entityLogs = allLogs
                    .filter(l => l.entity_id === id && l.entity_type === collectionName)
                    .sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
                setLogs(entityLogs);

                // 4. Load Comments
                const allComments = await commentsHelpers.fetchItems();
                const entityComments = allComments
                    .filter(c => c.entity_id === id && c.entity_type === collectionName)
                    .sort((a, b) => new Date(b.created_at || b.createdAt) - new Date(a.created_at || a.createdAt));
                setComments(entityComments);

                // 5. Load Details
                try {
                    const detailName = `${componentName}Details`;
                    // Try registry first
                    let dConfig = getConfig(detailName);

                    if (!dConfig) {
                        // Fallback import
                        const holdingFolder = pathify(main);
                        const subFolder = pathify(sub);
                        const detailModule = await import(`../components/Management/${holdingFolder}/${subFolder}/Details/${detailName}`);
                        dConfig = detailModule.default || detailModule;
                    }
                    setDetailConfig(dConfig);

                    if (dConfig) {
                        const detailEntityName = dConfig.collectionName || dConfig.entityName || `${entity}_details`;
                        const detailWrapper = helpersWrapper(detailEntityName);
                        const allDetails = await detailWrapper.fetchItems();
                        const relevantDetails = allDetails.filter(d => d.parent_id === id);

                        const enrichedDetails = await Promise.all(relevantDetails.map(async (det) => {
                            const detComments = allComments
                                .filter(c => c.entity_id === det.id && c.entity_type === detailEntityName)
                                .sort((a, b) => new Date(b.created_at || b.createdAt) - new Date(a.created_at || a.createdAt));
                            return { ...det, comments: detComments };
                        }));

                        setDetails(enrichedDetails);
                        generateDetailStats(enrichedDetails, dConfig.fieldsConfig);
                    }
                } catch (err) {
                    console.log("No details found or error loading details", err);
                }

                setLoading(false);

                // Print
                setTimeout(() => window.print(), 1500);

            } catch (err) {
                console.error("Error loading PDF data", err);
                setLoading(false);
            }
        };

        loadAll();
    }, [id, main, sub, entity, queryHelpers, logsHelpers, commentsHelpers]);

    const generateDetailStats = (rows, fieldsConfig) => {
        const generated = [];
        if (!fieldsConfig) return;
        Object.keys(fieldsConfig).forEach(field => {
            const fieldConfig = fieldsConfig[field];
            if (fieldConfig.options || ['status', 'type', 'category', 'priority'].some(k => field.includes(k))) {
                const counts = {};
                rows.forEach(r => {
                    const val = r[field] || 'Unknown';
                    counts[val] = (counts[val] || 0) + 1;
                });
                const data = Object.keys(counts).map(k => ({ name: k, value: counts[k] }));
                if (data.length > 0) generated.push({ title: fieldConfig.label || field, data: data, type: 'pie' });
            }
        });
        const numericFields = Object.keys(fieldsConfig).filter(k => fieldsConfig[k].type === 'number');
        if (numericFields.length > 0) {
            const sums = numericFields.map(f => {
                const sum = rows.reduce((acc, curr) => acc + (Number(curr[f]) || 0), 0);
                return { name: fieldsConfig[f].label, value: sum };
            });
            if (sums.some(s => s.value > 0)) generated.push({ title: 'Financial Aggregate', data: sums, type: 'bar' });
        }
        setStats(generated);
    };

    if (loading) return <Box sx={{ height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>Generating Report...</Box>;
    if (!data) return <Typography p={4} color="error">Error: Data could not be loaded.</Typography>;

    // Step Logic
    const steps = config?.stepsConfig?.steps || [];
    const currentStepIndex = steps.findIndex(s => s.name === data.processing_step) || 0;

    return (
        <Box sx={{
            bgcolor: '#525659', // Dark background for screen view to pop page
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: '40px',
            '@media print': {
                padding: 0,
                bgcolor: 'white',
                '& .a4-page': {
                    margin: 0,
                    boxShadow: 'none',
                    breakAfter: 'page',
                    pageBreakAfter: 'always'
                }
            }
        }}>
            <style>{`
                @media print {
                    @page { size: A4; margin: 0; }
                    body { margin: 0; }
                }
                .a4-page {
                    page-break-after: always;
                    break-after: page;
                }
            `}</style>

            {/* ================================================================================= */}
            {/* PAGE 1: HEADER, STEPS, DATA */}
            {/* ================================================================================= */}
            <Box className="a4-page" sx={styles.a4Page}>
                <Box sx={styles.headerStrip} />

                <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={4}>
                    <Box>
                        <Box display="flex" alignItems="center" gap={2} mb={1}>
                            <Typography variant="h4" sx={{ fontWeight: 900, color: '#1565c0' }}>
                                {config?.pageTitle || entity}
                            </Typography>
                        </Box>
                        <Typography variant="subtitle2" color="textSecondary">ID: {id}</Typography>
                    </Box>
                    <Box textAlign="right">
                        <Typography variant="caption" display="block" color="textSecondary">
                            CONFIDENTIAL REPORT<br />{new Date().toLocaleDateString()}
                        </Typography>
                    </Box>
                </Box>

                {/* Workflow Status Strip */}
                {steps.length > 0 && (
                    <Box sx={{ mb: 4 }}>
                        <Typography style={styles.sectionTitle}>Process Status</Typography>
                        <Box sx={{ display: 'flex', gap: 1, overflow: 'hidden' }}>
                            {steps.map((step, idx) => {
                                const isCompleted = idx <= currentStepIndex;
                                const isCurrent = idx === currentStepIndex;
                                return (
                                    <Box key={idx} sx={{
                                        flex: 1,
                                        p: 1,
                                        bgcolor: isCurrent ? '#1976d2' : (isCompleted ? '#e3f2fd' : '#f5f5f5'),
                                        color: isCurrent ? 'white' : (isCompleted ? '#1976d2' : '#aaa'),
                                        borderRadius: 1,
                                        textAlign: 'center',
                                        fontSize: '0.7rem',
                                        fontWeight: 700,
                                        border: isCurrent ? 'none' : '1px solid #eee'
                                    }}>
                                        {step.label || step.name}
                                    </Box>
                                )
                            })}
                        </Box>
                    </Box>
                )}

                <Box sx={styles.card}>
                    <Typography style={styles.sectionTitle}>Entity Data</Typography>
                    <Grid container spacing={2}>
                        {config?.fieldsConfig && Object.keys(config.fieldsConfig).map(key => {
                            if (key === 'created_at' || key === 'updated_at' || key === 'processing_step') return null;
                            const field = config.fieldsConfig[key];
                            return (
                                <Grid item xs={6} md={4} key={key}>
                                    <Box mb={1}>
                                        <Typography style={styles.label}>{field.label}</Typography>
                                        <Typography style={styles.value}>
                                            {field.type === 'date' || field.type.includes('time') ? formatDate(data[key]) : (data[key] || '—')}
                                        </Typography>
                                    </Box>
                                </Grid>
                            )
                        })}
                    </Grid>
                </Box>

                <Box mt="auto">
                    <Typography variant="caption" color="textSecondary" align="center" display="block">
                        Page 1 of Report • Companizer System
                    </Typography>
                </Box>
            </Box>

            {/* ================================================================================= */}
            {/* PAGE 2: WORKFLOW HISTORY & COMMENTS */}
            {/* ================================================================================= */}
            <Box className="a4-page" sx={styles.a4Page}>
                <Box sx={styles.headerStrip} />
                <Typography variant="h5" sx={{ fontWeight: 700, mb: 3, color: '#2c3e50' }}>Activity Record</Typography>

                <Grid container spacing={4} sx={{ height: '100%', overflow: 'hidden' }}>
                    {/* Left: Comments */}
                    <Grid item xs={7} sx={{ height: '90%', display: 'flex', flexDirection: 'column' }}>
                        <Typography style={styles.sectionTitle}>Comments</Typography>
                        <Box sx={{ flex: 1, overflow: 'hidden' }}>
                            {/* Note: In print, overflow hidden cuts content. This ensures "one page only". */}
                            {comments.length === 0 ? <Typography color="textSecondary">No comments.</Typography> : (
                                comments.map((c, i) => (
                                    <Box key={i} mb={2} display="flex" gap={2} sx={{ breakInside: 'avoid' }}>
                                        <Avatar sx={{ bgcolor: '#e1f5fe', color: '#0288d1', width: 24, height: 24, mt: 0.5 }}>
                                            <InsertComment style={{ fontSize: 14 }} />
                                        </Avatar>
                                        <Box flex={1}>
                                            <Box sx={{ bgcolor: '#f8f9fa', borderRadius: '4px', p: 1.5, border: '1px solid #eee' }}>
                                                <Typography variant="body2" sx={{ color: '#444', fontSize: '0.85rem' }}>{c.comment_text || c.text}</Typography>
                                            </Box>
                                            <Typography variant="caption" color="textSecondary" sx={{ ml: 1 }}>
                                                {c.author_name || 'User'} • {formatDate(c.created_at)}
                                            </Typography>
                                        </Box>
                                    </Box>
                                ))
                            )}
                        </Box>
                    </Grid>

                    {/* Right: Workflow Log */}
                    <Grid item xs={5} sx={{ height: '90%', display: 'flex', flexDirection: 'column' }}>
                        <Typography style={styles.sectionTitle}>Workflow Log</Typography>
                        <Box sx={{ flex: 1, overflow: 'hidden', position: 'relative', pl: 2, borderLeft: '2px solid #e0e0e0' }}>
                            {logs.length === 0 ? <Typography color="textSecondary">No history.</Typography> : (
                                logs.map((l, i) => (
                                    <Box key={i} mb={3} sx={{ position: 'relative', breakInside: 'avoid' }}>
                                        <Box sx={{
                                            position: 'absolute', left: -21, top: 2, width: 8, height: 8,
                                            borderRadius: '50%', bgcolor: '#555', border: '2px solid white'
                                        }} />
                                        <Typography variant="caption" display="block" sx={{ color: '#999', fontSize: '0.7rem' }}>
                                            {formatDate(l.created_at)}
                                        </Typography>
                                        <Typography variant="subtitle2" sx={{ fontWeight: 700, fontSize: '0.85rem' }}>{l.action_type}</Typography>
                                        <Typography variant="body2" color="textSecondary" sx={{ fontSize: '0.75rem' }}>{l.details}</Typography>
                                    </Box>
                                ))
                            )}
                        </Box>
                    </Grid>
                </Grid>
            </Box>

            {/* ================================================================================= */}
            {/* DETAILS PAGES */}
            {/* One Page Per Detail */}
            {/* ================================================================================= */}
            {details.map((item, idx) => (
                <Box key={idx} className="a4-page" sx={styles.a4Page}>
                    <Box sx={{ ...styles.headerStrip, background: 'linear-gradient(90deg, #6c5ce7 0%, #a29bfe 100%)' }} />

                    <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                        <Typography variant="h5" sx={{ fontWeight: 800, color: '#6c5ce7' }}>
                            Detail Record #{idx + 1}
                        </Typography>
                    </Box>

                    <Box sx={styles.card}>
                        <Grid container spacing={2}>
                            {detailConfig?.fieldsConfig && Object.keys(detailConfig.fieldsConfig).map(key => {
                                const field = detailConfig.fieldsConfig[key];
                                return (
                                    <Grid item xs={6} key={key}>
                                        <Box margin={0.5}>
                                            <Typography style={{ ...styles.label, color: '#b2bec3' }}>{field.label}</Typography>
                                            <Typography style={styles.value}>{item[key] || '—'}</Typography>
                                        </Box>
                                    </Grid>
                                )
                            })}
                        </Grid>
                    </Box>

                    <Box mt={2} flex={1} overflow="hidden">
                        <Typography style={{ ...styles.sectionTitle, color: '#6c5ce7', borderColor: '#e1d5f5' }}>Notes</Typography>
                        {item.comments && item.comments.length > 0 ? (
                            item.comments.map((c, ci) => (
                                <Box key={ci} mb={2} p={1.5} sx={{ bgcolor: '#fdfdff', borderLeft: '3px solid #6c5ce7', breakInside: 'avoid' }}>
                                    <Typography variant="body2" sx={{ fontStyle: 'italic', color: '#555', fontSize: '0.85rem' }}>"{c.comment_text || c.text}"</Typography>
                                    <Box mt={0.5} display="flex" gap={1} alignItems="center">
                                        <Typography variant="caption" color="textSecondary">{c.author_name} • {formatDate(c.created_at)}</Typography>
                                    </Box>
                                </Box>
                            ))
                        ) : <Typography color="textSecondary" fontSize="0.8rem">No specific notes.</Typography>}
                    </Box>
                </Box>
            ))}

            {/* ================================================================================= */}
            {/* STATISTICS PAGE */}
            {/* ================================================================================= */}
            {stats.length > 0 && (
                <Box className="a4-page" sx={styles.a4Page}>
                    <Box sx={styles.headerStrip} />
                    <Typography variant="h5" sx={{ fontWeight: 800, mb: 4, textAlign: 'center', color: '#2c3e50' }}>Analytics Dashboard</Typography>

                    <Grid container spacing={4} justifyContent="center" sx={{ flex: 1 }}>
                        {stats.map((stat, i) => (
                            <Grid item xs={12} key={i}>
                                <Box sx={{ p: 2, border: '1px solid #eee', borderRadius: 2, height: '250px' }}>
                                    <Typography variant="subtitle1" align="center" gutterBottom sx={{ fontWeight: 700 }}>
                                        {stat.title}
                                    </Typography>
                                    <ResponsiveContainer width="100%" height="80%">
                                        {stat.type === 'bar' ? (
                                            <BarChart data={stat.data}>
                                                <CartesianGrid strokeDasharray="3 3" vertical={false} />
                                                <XAxis dataKey="name" tick={{ fontSize: 10 }} />
                                                <YAxis tick={{ fontSize: 10 }} />
                                                <RechartsTooltip />
                                                <Bar dataKey="value" fill="#6c5ce7" barSize={40} />
                                            </BarChart>
                                        ) : (
                                            <PieChart>
                                                <Pie
                                                    data={stat.data}
                                                    cx="50%"
                                                    cy="50%"
                                                    innerRadius={40}
                                                    outerRadius={70}
                                                    dataKey="value"
                                                    label
                                                >
                                                    {stat.data.map((entry, index) => (
                                                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                                    ))}
                                                </Pie>
                                                <Legend wrapperStyle={{ fontSize: '10px' }} />
                                            </PieChart>
                                        )}
                                    </ResponsiveContainer>
                                </Box>
                            </Grid>
                        ))}
                    </Grid>
                </Box>
            )}

            {/* ================================================================================= */}
            {/* RGPD PAGE */}
            {/* ================================================================================= */}
            <Box className="a4-page" sx={{ ...styles.a4Page, justifyContent: 'center', alignItems: 'center' }}>
                <Box sx={{
                    border: '4px solid #333',
                    padding: '40px',
                    maxWidth: '80%',
                    textAlign: 'center',
                }}>
                    <Typography variant="h4" gutterBottom sx={{ fontWeight: 900, letterSpacing: '0.2em', mb: 4 }}>OFFICIAL DOCUMENT</Typography>

                    <Typography variant="body1" paragraph sx={{ fontSize: '1rem', lineHeight: 1.8 }}>
                        This document contains confidential information regulated by the <strong>General Data Protection Regulation (GDPR)</strong>.
                    </Typography>

                    <Typography variant="body2" color="textSecondary" paragraph>
                        Unauthorized access or reproduction is prohibited.
                    </Typography>

                    <Box mt={6} display="flex" justifyContent="center" gap={8} textAlign="left">
                        <Box>
                            <Box sx={{ width: 200, borderBottom: '2px solid black', mb: 1, height: 50 }} />
                            <Typography variant="caption" sx={{ fontWeight: 700 }}>AUTHORIZED SIGNATURE</Typography>
                        </Box>
                        <Box>
                            <Box sx={{ width: 120, borderBottom: '2px solid black', mb: 1, height: 50 }} />
                            <Typography variant="caption" sx={{ fontWeight: 700 }}>DATE</Typography>
                        </Box>
                    </Box>
                </Box>
            </Box>

        </Box>
    );
};

export default ConfigurablePdf;

import React, { useEffect } from 'react';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';  // Import Gantt CSS
import gantt from 'dhtmlx-gantt';        // Import Gantt JavaScript
import { Container } from '@mui/material';

// Function to generate random data
const generateRandomTasks = (numTasks) => {
  const tasks = [];
  const now = new Date();

  for (let i = 0; i < numTasks; i++) {
    const startOffset = Math.floor(Math.random() * 30); // Random offset for start date
    const duration = Math.floor(Math.random() * 10) + 1; // Random duration between 1 and 10 days
    const progress = Math.random(); // Random progress between 0 and 1

    tasks.push({
      id: i + 1,
      text: `Task ${i + 1}`,
      start_date: new Date(now.getTime() + startOffset * 24 * 60 * 60 * 1000).toLocaleDateString('en-GB'), // Format to correct date string
      duration: duration,
      progress: progress,
    });
  }

  return tasks;
};

const GanttChart = () => {
  useEffect(() => {
    gantt.init('gantt_here');  // Initialize the Gantt chart

    const tasks = {
      data: generateRandomTasks(10), // Generate 10 random tasks
      links: [], // Optional: add links if necessary
    };

    gantt.parse(tasks);  // Parse tasks to Gantt chart

    // Clean up on component unmount
    return () => gantt.clearAll();
  }, []);

  return (
      <div
        id="gantt_here"
        style={{
          height: '80vh',
          width: '80vw',
        }}
      ></div>
  );
};

export default GanttChart;

import React, { useMemo, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
    Box,
    Card,
    CardContent,
    Typography,
    Grid,
    Paper,
    Divider,
    List,
    ListItem,
    ListItemText,
    ListItemIcon,
    Avatar,
    Button,
    CircularProgress,
    Alert
} from '@mui/material';
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend,
    ResponsiveContainer,
    PieChart,
    Pie,
    Cell,
    LineChart,
    Line,
} from 'recharts';
import {
    TrendingUp,
    Category,
    Timeline,
} from '@mui/icons-material';
import analysisRegistry from '../analysisRegistry';
import { helpersWrapper } from '../utils/clientQueries';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

// Helper to format currency
const formatCurrency = (value) =>
    new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(value);

const GenericAnalyticsDashboard = (props) => {
    const { entity } = useParams();

    // Determine configuration and data source
    // Priority: Props -> analysisRegistry based on param

    const [fetchedData, setFetchedData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const config = useMemo(() => {
        if (props.fieldsConfig && props.collectionName) {
            return {
                fieldsConfig: props.fieldsConfig,
                collectionName: props.collectionName,
                title: props.title,
                modules: props.modules || []
            };
        }
        if (entity && analysisRegistry[entity]) {
            return analysisRegistry[entity];
        }
        return null;
    }, [props, entity]);

    const helpers = useMemo(() => {
        if (config && config.collectionName) {
            return helpersWrapper(config.collectionName);
        }
        return null;
    }, [config]);

    useEffect(() => {
        // If data is passed via props, use it (handled in derived state/render), no fetch needed usually.
        // But here we want to support fetching if data is NOT passed.

        if (props.data) {
            setFetchedData(props.data);
            return;
        }

        if (!config || !helpers) {
            return;
        }

        let isMounted = true;
        setLoading(true);
        setError(null);

        helpers.fetchItems()
            .then(items => {
                if (isMounted) {
                    setFetchedData(items || []);
                    setLoading(false);
                }
            })
            .catch(err => {
                console.error("Error loading analysis data:", err);
                if (isMounted) {
                    setError("Failed to load data.");
                    setLoading(false);
                }
            });

        return () => { isMounted = false; };
    }, [helpers, config, props.data]);

    const data = fetchedData;
    const { fieldsConfig, collectionName } = config || {};

    // All hooks must be called unconditionally, before any early returns
    // 1. KPIs (numeric aggregations)
    const kpis = useMemo(() => {
        if (!data.length || !fieldsConfig) return [];

        const numericFields = Object.keys(fieldsConfig).filter(
            key => fieldsConfig[key].type === 'number' && !key.includes('id') && key !== 'unit_price'
        );

        // Always include unit_price separately if present, or as a main KPI if it's the only one
        if (fieldsConfig.unit_price) numericFields.push('unit_price');

        return numericFields.map(field => {
            const sum = data.reduce((acc, item) => acc + (Number(item[field]) || 0), 0);
            const avg = sum / data.length;
            return {
                key: field,
                label: fieldsConfig[field].label,
                sum,
                avg,
                min: Math.min(...data.map(i => Number(i[field]) || 0)),
                max: Math.max(...data.map(i => Number(i[field]) || 0))
            };
        });
    }, [data, fieldsConfig]);

    // 2. Distributions (Pie Charts) for categorical fields (Selects)
    const distributions = useMemo(() => {
        if (!data.length || !fieldsConfig) return [];

        const categoricalFields = Object.keys(fieldsConfig).filter(
            key => fieldsConfig[key].type === 'select'
        );

        return categoricalFields.map(field => {
            const counts = data.reduce((acc, item) => {
                const val = item[field] || 'Unknown';
                acc[val] = (acc[val] || 0) + 1;
                return acc;
            }, {});

            return {
                key: field,
                label: fieldsConfig[field].label,
                data: Object.keys(counts).map(k => ({ name: k, value: counts[k] }))
            };
        });
    }, [data, fieldsConfig]);

    // 3. Trends (Line Charts) based on Date fields
    const trends = useMemo(() => {
        if (!data.length || !fieldsConfig) return { label: '', data: [] };

        const dateField = Object.keys(fieldsConfig).find(
            key => fieldsConfig[key].type === 'date' || fieldsConfig[key].type === 'datetime'
        ) || 'created_at';

        // Group by month/year
        const timeGrouped = data.reduce((acc, item) => {
            let d = item[dateField];
            if (!d && item.created_at) d = item.created_at;
            if (!d) return acc;

            const dateObj = new Date(d);
            const key = dateObj.toISOString().slice(0, 7); // YYYY-MM
            acc[key] = (acc[key] || 0) + 1;
            return acc;
        }, {});

        const sortedData = Object.keys(timeGrouped).sort().map(key => ({
            date: key,
            count: timeGrouped[key]
        }));

        return {
            label: `Trend over time (${fieldsConfig[dateField]?.label || 'Date'})`,
            data: sortedData
        };
    }, [data, fieldsConfig]);

    // 4. Insights Generation (Simple Rules)
    const insights = useMemo(() => {
        if (!data.length) return ["No data available to generate insights."];

        const generated = [];
        generated.push(`Total ${data.length} records analyzed.`);

        kpis.forEach(kpi => {
            generated.push(`Total ${kpi.label}: ${kpi.key.includes('price') || kpi.key.includes('amount') ? formatCurrency(kpi.sum) : kpi.sum.toLocaleString()}`);
        });

        distributions.forEach(dist => {
            const top = dist.data.sort((a, b) => b.value - a.value)[0];
            if (top) generated.push(`Most common ${dist.label}: ${top.name} (${top.value} occurrences)`);
        });

        return generated;
    }, [data, kpis, distributions]);

    // Early returns after all hooks have been called
    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box p={3}>
                <Alert severity="error">{error}</Alert>
            </Box>
        );
    }

    if (!config) {
        return (
            <Box p={3}>
                <Alert severity="warning">No configuration found for analytics.</Alert>
            </Box>
        );
    }

    if (!data || data.length === 0) {
        return (
            <Box p={3}>
                <Typography variant="h6" color="textSecondary">No data available for analysis.</Typography>
            </Box>
        );
    }

    return (
        <Box sx={{ flexGrow: 1, p: 3 }}>
            <Box display="flex" alignItems="center" mb={4}>
                <Typography variant="h4" component="div" sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Avatar sx={{ bgcolor: 'secondary.main' }}><Timeline /></Avatar>
                    Analytics: {collectionName}
                </Typography>
            </Box>

            {/* KPI Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
                <Grid item xs={12} sm={6} md={3}>
                    <Card sx={{ height: '100%', bgcolor: '#e3f2fd' }}>
                        <CardContent>
                            <Typography color="textSecondary" gutterBottom>Total Records</Typography>
                            <Typography variant="h4">{data.length}</Typography>
                        </CardContent>
                    </Card>
                </Grid>
                {kpis.slice(0, 3).map((kpi, index) => (
                    <Grid item xs={12} sm={6} md={3} key={index}>
                        <Card sx={{ height: '100%' }}>
                            <CardContent>
                                <Typography color="textSecondary" gutterBottom>{kpi.label} (Sum)</Typography>
                                <Typography variant="h4" sx={{ color: 'primary.main' }}>
                                    {kpi.key.includes('price') || kpi.key.includes('cost') || kpi.key.includes('budget')
                                        ? formatCurrency(kpi.sum)
                                        : kpi.sum.toLocaleString()}
                                </Typography>
                                <Typography variant="caption" display="block">
                                    Avg: {kpi.key.includes('price') ? formatCurrency(kpi.avg) : kpi.avg.toFixed(2)}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            <Grid container spacing={3}>
                {/* Trends Chart */}
                <Grid item xs={12} md={8}>
                    <Paper sx={{ p: 2, display: 'flex', flexDirection: 'column', height: 400 }}>
                        <Typography variant="h6" gutterBottom>{trends.label}</Typography>
                        <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={trends.data}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="date" />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="count" stroke="#8884d8" activeDot={{ r: 8 }} />
                            </LineChart>
                        </ResponsiveContainer>
                    </Paper>
                </Grid>

                {/* Insights Panel */}
                <Grid item xs={12} md={4}>
                    <Paper sx={{ p: 2, height: 400, overflow: 'auto', bgcolor: '#fff3e0' }}>
                        <Typography variant="h6" gutterBottom display="flex" alignItems="center" gap={1}>
                            <TrendingUp /> Key Insights
                        </Typography>
                        <List>
                            {insights.map((text, i) => (
                                <React.Fragment key={i}>
                                    <ListItem>
                                        <ListItemIcon><Category fontSize="small" /></ListItemIcon>
                                        <ListItemText primary={text} />
                                    </ListItem>
                                    <Divider component="li" />
                                </React.Fragment>
                            ))}
                        </List>
                    </Paper>
                </Grid>

                {/* Distribution Charts */}
                {distributions.map((dist, index) => (
                    <Grid item xs={12} md={6} lg={4} key={index}>
                        <Paper sx={{ p: 2, height: 350, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                            <Typography variant="h6" gutterBottom>{dist.label} Distribution</Typography>
                            <ResponsiveContainer width="100%" height="100%">
                                <PieChart>
                                    <Pie
                                        data={dist.data}
                                        cx="50%"
                                        cy="50%"
                                        innerRadius={60}
                                        outerRadius={80}
                                        fill="#8884d8"
                                        paddingAngle={5}
                                        dataKey="value"
                                        label
                                    >
                                        {dist.data.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                        ))}
                                    </Pie>
                                    <Tooltip />
                                    <Legend />
                                </PieChart>
                            </ResponsiveContainer>
                        </Paper>
                    </Grid>
                ))}

                {/* Bar Charts for Numeric Aggregates by Category (if feasible) */}
                {distributions.length > 0 && kpis.length > 0 && (
                    <Grid item xs={12}>
                        <Paper sx={{ p: 2, height: 400 }}>
                            <Typography variant="h6" gutterBottom>
                                {kpis[0].label} by {distributions[0].label}
                            </Typography>
                            {/* We need to aggregate comparison data here, but for now let's just show a simple bar chart of the first distribution counts as a placeholder for complex cross-analysis */}
                            <ResponsiveContainer width="100%" height="100%">
                                <BarChart data={distributions[0].data}>
                                    <CartesianGrid strokeDasharray="3 3" />
                                    <XAxis dataKey="name" />
                                    <YAxis />
                                    <Tooltip />
                                    <Legend />
                                    <Bar dataKey="value" fill="#82ca9d" name="Count" />
                                </BarChart>
                            </ResponsiveContainer>
                        </Paper>
                    </Grid>
                )}

            </Grid>
        </Box>
    );
};

export default GenericAnalyticsDashboard;

import React, { useState, useEffect } from 'react';
import {
    Container,
    Box,
    Typography,
    Tabs,
    Tab,
    Card,
    CardContent,
    CardActions,
    Button,
    Chip,
    Grid,
    IconButton,
    Menu,
    MenuItem,
    Alert
} from '@mui/material';
import { MoreVert, CheckCircle, PlayArrow, OpenInNew } from '@mui/icons-material';
import { format } from 'date-fns';
import { useNavigate } from 'react-router-dom';
import { helpersWrapper } from '../utils/clientQueries';
import { useAuthorization } from '../hooks/useAuthorization';
import useNotifications from '../hooks/useNotifications';

const TaskManager = () => {
    const navigate = useNavigate();
    const { currentUser } = useAuthorization();
    const { createNotification } = useNotifications(currentUser?.id);

    const [tasks, setTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentTab, setCurrentTab] = useState(0);
    const [anchorEl, setAnchorEl] = useState(null);
    const [selectedTask, setSelectedTask] = useState(null);

    const taskHelper = helpersWrapper('tasks');

    useEffect(() => {
        loadTasks();
    }, [currentUser]);

    const loadTasks = async () => {
        if (!currentUser) return;

        try {
            const allTasks = await taskHelper.fetchItems();
            const userTasks = allTasks.filter(t => t.assigned_to === currentUser.id);
            setTasks(userTasks);
            setLoading(false);
        } catch (error) {
            console.error('Failed to load tasks:', error);
            setLoading(false);
        }
    };

    const handleMenuClick = (event, task) => {
        setAnchorEl(event.currentTarget);
        setSelectedTask(task);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
        setSelectedTask(null);
    };

    const updateTaskStatus = async (taskId, newStatus) => {
        try {
            await taskHelper.updateItem(taskId, { status: newStatus });

            // Create notification for task assigner
            const task = tasks.find(t => t.id === taskId);
            if (task && task.assigned_by_user_id) {
                await createNotification({
                    userId: task.assigned_by_user_id,
                    type: 'task_completed',
                    title: 'Task Updated',
                    message: `${currentUser.first_name} ${currentUser.last_name} marked task "${task.title}" as ${newStatus}`,
                    relatedTaskId: taskId,
                    relatedEntityType: task.related_entity_type,
                    relatedEntityId: task.related_entity_id
                });
            }

            loadTasks();
            handleMenuClose();
        } catch (error) {
            console.error('Failed to update task:', error);
        }
    };

    const navigateToEntity = (task) => {
        if (task.related_entity_type && task.related_entity_id) {
            // Navigate to the related entity
            navigate(`/management/${task.related_entity_type}?view=${task.related_entity_id}`);
        }
    };

    const getPriorityColor = (priority) => {
        switch (priority) {
            case 'urgent': return 'error';
            case 'high': return 'warning';
            case 'medium': return 'info';
            case 'low': return 'default';
            default: return 'default';
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'completed': return 'success';
            case 'in_progress': return 'primary';
            case 'overdue': return 'error';
            case 'pending': return 'warning';
            default: return 'default';
        }
    };

    const filterTasks = () => {
        switch (currentTab) {
            case 0: // All
                return tasks;
            case 1: // Pending
                return tasks.filter(t => t.status === 'pending');
            case 2: // In Progress
                return tasks.filter(t => t.status === 'in_progress');
            case 3: // Completed
                return tasks.filter(t => t.status === 'completed');
            default:
                return tasks;
        }
    };

    const filteredTasks = filterTasks();

    if (loading) {
        return (
            <Container maxWidth="xl" sx={{ py: 4 }}>
                <Typography>Loading tasks...</Typography>
            </Container>
        );
    }

    return (
        <Container maxWidth="xl" sx={{ py: 4 }}>
            <Box sx={{ mb: 4 }}>
                <Typography variant="h4" sx={{ fontWeight: 700, mb: 1 }}>
                    My Tasks
                </Typography>
                <Typography color="text.secondary">
                    Manage your assigned tasks and track progress
                </Typography>
            </Box>

            {/* Tabs */}
            <Tabs value={currentTab} onChange={(e, v) => setCurrentTab(v)} sx={{ mb: 3 }}>
                <Tab label={`All (${tasks.length})`} />
                <Tab label={`Pending (${tasks.filter(t => t.status === 'pending').length})`} />
                <Tab label={`In Progress (${tasks.filter(t => t.status === 'in_progress').length})`} />
                <Tab label={`Completed (${tasks.filter(t => t.status === 'completed').length})`} />
            </Tabs>

            {/* Task Cards */}
            {filteredTasks.length === 0 ? (
                <Alert severity="info">No tasks in this category</Alert>
            ) : (
                <Grid container spacing={2}>
                    {filteredTasks.map((task) => (
                        <Grid item xs={12} md={6} lg={4} key={task.id}>
                            <Card elevation={2}>
                                <CardContent>
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                        <Chip
                                            label={task.status.replace('_', ' ').toUpperCase()}
                                            size="small"
                                            color={getStatusColor(task.status)}
                                        />
                                        <IconButton size="small" onClick={(e) => handleMenuClick(e, task)}>
                                            <MoreVert />
                                        </IconButton>
                                    </Box>

                                    <Typography variant="h6" sx={{ mb: 1 }}>
                                        {task.title}
                                    </Typography>

                                    {task.description && (
                                        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                                            {task.description}
                                        </Typography>
                                    )}

                                    <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                                        <Chip
                                            label={task.priority.toUpperCase()}
                                            size="small"
                                            color={getPriorityColor(task.priority)}
                                            variant="outlined"
                                        />
                                        {task.sla_deadline && (
                                            <Chip
                                                label={`Due: ${format(new Date(task.sla_deadline), 'MMM d')}`}
                                                size="small"
                                                variant="outlined"
                                            />
                                        )}
                                    </Box>

                                    {task.related_entity_type && (
                                        <Typography variant="caption" color="text.secondary">
                                            Related to: {task.related_entity_type.replace(/_/g, ' ')}
                                        </Typography>
                                    )}
                                </CardContent>

                                <CardActions>
                                    {task.status !== 'in_progress' && task.status !== 'completed' && (
                                        <Button
                                            size="small"
                                            startIcon={<PlayArrow />}
                                            onClick={() => updateTaskStatus(task.id, 'in_progress')}
                                        >
                                            Start
                                        </Button>
                                    )}
                                    {task.status === 'in_progress' && (
                                        <Button
                                            size="small"
                                            startIcon={<CheckCircle />}
                                            onClick={() => updateTaskStatus(task.id, 'completed')}
                                        >
                                            Complete
                                        </Button>
                                    )}
                                    {task.related_entity_type && (
                                        <Button
                                            size="small"
                                            startIcon={<OpenInNew />}
                                            onClick={() => navigateToEntity(task)}
                                        >
                                            View Entity
                                        </Button>
                                    )}
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            )}

            {/* Task Menu */}
            <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleMenuClose}
            >
                {selectedTask?.status !== 'in_progress' && (
                    <MenuItem onClick={() => updateTaskStatus(selectedTask.id, 'in_progress')}>
                        Start Task
                    </MenuItem>
                )}
                {selectedTask?.status === 'in_progress' && (
                    <MenuItem onClick={() => updateTaskStatus(selectedTask.id, 'completed')}>
                        Mark Complete
                    </MenuItem>
                )}
                {selectedTask?.status === 'completed' && (
                    <MenuItem onClick={() => updateTaskStatus(selectedTask.id, 'in_progress')}>
                        Reopen Task
                    </MenuItem>
                )}
            </Menu>
        </Container>
    );
};

